            string Nombre_Archivo;
            Nombre_Archivo = Dts.Variables["User::V_Nombre_Archivo"].Value.ToString(); // pasamos la variable de SSIS a una variable de C#

            string[] lista_archivos = {"CCPLPE.TXT","CTITPE.TXT","GRIPRO.TXT","HAFACT.TXT","HALTAP.TXT","HALTAT.TXT",
                                        "HBAJAP.TXT","HBAJAT.TXT","HCPLPD.TXT","HFACTI.TXT","HTISPD.TXT"};
            //  en esta lista de archivos tienen que ir todos los nombres de los archivos que queremos tomar en el proceso


            // si la bandera = 1 indica que el archivo tiene que ser tomado
            // si la Bandera = 0 indica que no se va a tomar.
            Dts.Variables["User::V_Bandera_Flujo"].Value = Convert.ToInt32(0);
            foreach (var item in lista_archivos)
            {
                if (Nombre_Archivo == item)
                {
                    Dts.Variables["User::V_Bandera_Flujo"].Value = Convert.ToInt32(1);
                }
            }

            Dts.TaskResult = (int)ScriptResults.Success;
		}

