/* Actualiza H_AFILIADOS_EMPRESA_LABORAL */
DELETE H_AFILIADOS_EMPRESA_LABORAL where id_periodo = (select max(id_periodo) from AUX_PERIODO_MES_BORRA)
INSERT INTO H_AFILIADOS_EMPRESA_LABORAL SELECT id_periodo, afiliado, subnumero, secuencia, id_persona, 'E' as Id_Tipo_Entidad, id_empresa, 1 as cant_pers_empresa_laboral, CANT_INTEGRANTES FROM TEMP_H_AFILIADOS_EMPRESAS_LABORAL WHERE CUIL_TITULAR <> 0 and SECUENCIA <> 99 ORDER BY ID_PERSONA
