 not (a11.Id_Producto = 14 and a11.Id_Plan_Producto = 'CO ')) group by a11.id_periodo, a11.id_persona, a12.edad_actual, a11.Id_Producto, a11.Id_Plan_Producto, a11.id_entidad, a12.id_localidad_afiliado, a13.id_provincia, a11.id_promotor, a222.de_promotor, a11.id_cobertura, a13.id_zona_comercial, a17.de_os_proveniente, a11.afiliado, a11.Subnumero, a11.secuencia
-- Calcula si es Titular o NO
UPDATE ALTAS SET Id_Titular = 'S' FROM ALTAS A INNER JOIN DW_SALUD.dbo.H_COMER_GRUPO_ACTIVO H ON A.id_persona = H.id_persona AND H.Id_Periodo = @PERIODO_ANTERIOR;
UPDATE ALTAS SET Id_Titular = 'N' where Id_Titular is null
--calcular cuenta del stock
--UPDATE ALTAS SET Cuenta = H.Afiliado, SubCuenta = H.Subnumero, Secuencia = H.Secuencia --FROM ALTAS A --INNER JOIN DW_SALUD.dbo.H_COMER_STOCK_AFILIA H ON A.id_persona = H.id_persona AND A.id_producto = H.id_producto AND A.id_plan_producto = H.id_plan_producto AND H.Id_Periodo = @PERIODO_ANTERIOR;
-- Calcular la cantidad que integran el Grupo FAMILIAR
UPDATE ALTAS SET Cant_Grupo_Familiar = H.Grupo FROM ALTAS A INNER JOIN (SELECT afiliado, id_producto, id_plan_producto, COUNT(*) Grupo FROM DW_SALUD.DBO.H_COMER_STOCK_AFILIA where id_periodo = @PERIODO_ANTERIOR GROUP BY Id_Periodo, afiliado, id_producto, id_plan_producto) H ON A.cuenta = H.afiliado AND A.id_producto = H.id_producto AND A.id_plan_Producto = H.id_plan_Producto
-- Busco Celular 1 y 2 de cada Persona
UPDATE ALTAS SET Celular_Caracteristica_1 = H.W03CAR, Celular_Numero_1 = H.W03NRO FROM ALTAS A INNER JOIN (SELECT W03NPE, W03CAR, W03NRO FROM [HOST03].[HOST03].[SMPLIBD].[M03FTEL] WHERE W03TIT = 'C' AND W03COT = 1) H ON A.Id_Persona = H.W03NPE;
UPDATE ALTAS SET Celular_Caracteristica_2 = H.W03CAR, Celular_Numero_2 = H.W03NRO FROM ALTAS A INNER JOIN (SELECT W03NPE, W03CAR, W03NRO FROM [HOST03].[HOST03].[SMPLIBD].[M03FTEL] WHERE W03TIT = 'C' AND W03COT = 2) H ON A.Id_Persona = H.W03NPE;
-- Busco EMAIL de cada Persona
UPDATE ALTAS SET Mail = H.W03NRO FROM ALTAS A INNER JOIN (SELECT W03NPE, W03NRO FROM [HOST03].[HOST03].[SMPLIBD].[M03FTEL] WHERE W03TIT = 'E') H ON A.Id_Persona = H.W03NPE;
-- Actualizo CARENCIAS del Afiliado
UPDATE ALTAS SET Carencias_1 = H.W11C01, Carencias_2 = H.W11C02, Carencias_3 = H.W11C03, Carencias_4 = H.W11C04 FROM ALTAS A INNER JOIN (SELECT W11NAF, W11SAF, W11SEC, W11C01, W11C02, W11C03, W11C04 FROM [HOST03].[HOST03].[SMPLIBD].[M11FCAR]) H ON A.Cuenta = H.W11NAF AND A.SubCuenta = H.W11SAF AND A.Secuencia = H.W11SEC;
-- Actualizo EMPRESAS LABORALES de la PERSONA - SOLAMENTE 2 EMPRESAS SE TIENEN EN CUENTA
INSERT INTO PERSONAS_EMPRESAS Select id_persona, EMP5.W05EMP, EMP5.W05DEN, CASE ISDATE(Substring(CAST(EMP7.WA7FIN as CHAR(8)),1,4)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),5,2)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),7,2)) WHEN 0 THEN '1900-01-01' ELSE Substring(CAST(EMP7.WA7FIN as CHAR(8)),1,4)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),5,2)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),7,2) END FROM ALTAS A INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M06FAFI] AFI ON A.id_persona = AFI.W06NPE INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M07FOSO] EMP7 ON AFI.W06NAF = EMP7.WA7NAF AND AFI.W06SAF = EMP7.WA7SAF INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M05FEMP] EMP5 ON EMP7.WA7EMP = EMP5.W05EMP WHERE EMP7.WA7FBA = 0 GROUP BY id_persona, EMP5.W05EMP, EMP5.W05DEN, CASE ISDATE(Substring(CAST(EMP7.WA7FIN as CHAR(8)),1,4)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),5,2)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),7,2)) WHEN 0 THEN '1900-01-01' ELSE Substring(CAST(EMP7.WA7FIN as CHAR(8)),1,4)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),5,2)+'-'+Substring(CAST(EMP7.WA7FIN as CHAR(8)),7,2) END order by id_persona;
INSERT INTO Empresas_1 SELECT PE.Id_Persona, PE.ID_Empresa FROM PERSONAS_EMPRESAS PE INNER JOIN (select Id_Persona, MAX(Fecha_Ingreso) Fecha_Max_Ingreso from PERSONAS_EMPRESAS GROUP BY Id_Persona) PMAX_FI ON PE.ID_Persona = PMAX_FI.Id_Persona AND PE.Fecha_Ingreso = PMAX_FI.Fecha_Max_Ingreso;
DELETE PERSONAS_EMPRESAS FROM PERSONAS_EMPRESAS PE INNER JOIN (select Id_Persona, MAX(Fecha_Ingreso) Fecha_Max_Ingreso from PERSONAS_EMPRESAS GROUP BY Id_Persona) PMAX_FI ON PE.ID_Persona = PMAX_FI.Id_Persona AND PE.Fecha_Ingreso = PMAX_FI.Fecha_Max_Ingreso;
INSERT INTO Empresas_2 SELECT PE.Id_Persona, Pe.ID_Empresa FROM PERSONAS_EMPRESAS PE INNER JOIN (select Id_Persona, MAX(Fecha_Ingreso) Fecha_Max_Ingreso from PERSONAS_EMPRESAS GROUP BY Id_Persona) PMAX_FI ON PE.ID_Persona = PMAX_FI.Id_Persona AND PE.Fecha_Ingreso = PMAX_FI.Fecha_Max_Ingreso;
INSERT INTO EMPRESAS_FINAL Select E1.Id_Persona, Id_Empresa_1, ' ' as Desc_Empresa_1, Id_Empresa_2, '' as Desc_Empresa_2 FROM Empresas_1 E1 INNER JOIN Empresas_2 E2 ON E1.Id_Persona = E2.Id_Persona;
UPDATE EMPRESAS_FINAL SET Desc_Empresa_1 = W05DEN FROM EMPRESAS_FINAL INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M05FEMP] EMP ON EMPRESAS_FINAL.Id_Empresa_1 = EMP.W05EMP;
UPDATE EMPRESAS_FINAL SET Desc_Empresa_2 = W05DEN FROM EMPRESAS_FINAL INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M05FEMP] EMP ON EMPRESAS_FINAL.Id_Empresa_2 = EMP.W05EMP;
UPDATE ALTAS SET Id_Empresa_1 = E.Id_Empresa_1, Desc_Empresa_1 = E.Desc_Empresa_1, Id_Empresa_2 = E.Id_Empresa_2, Desc_Empresa_2 = E.Desc_Empresa_2 FROM ALTAS A INNER JOIN EMPRESAS_FINAL E ON A.Id_PErsona = E.Id_Persona;
