UPDATE ALTAS SET Id_GA = GA.WL7GPO FROM ALTAS A INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M07FSAL] GA ON A.Cuenta = GA.WL7NAF AND A.Subcuenta = GA.WL7SAF
UPDATE ALTAS SET Desc_GA = GA.W86DES FROM ALTAS A INNER JOIN [HOST03].[HOST03].[SMPLIBD].[T186FGAF] GA ON A.Id_GA = GA.W86GPO
