UPDATE ALTAS SET de_cobertura = CAST(H.de_os_proveniente as NVARCHAR(50)) FROM ALTAS A inner join DW_SALUD.dbo.D_OS_PROVENIENTE H on A.id_cobertura = H.id_os_proveniente
select Cuenta, count(Cuenta) as Contador_Grupo INTO Cuentas_REPETIDAS from altas group by Cuenta having count(Cuenta) > 1
/* Actualizo Valor Grilla */
/* Tener en cuenta que si la fecha en que se ejecuta el proceso supera la fecha (el 15 aprox) en que se ejecuta la liquidacion hay que buscar de las H01FCAB y REN y no de las C01FCAB y REN de liquidaciones */
SELECT m.Id_Periodo, LC.afiliado as Afiliado, SUM(LC.total_concepto) as Valor_Grilla INTO ValGril FROM altas M JOIN DW_SALUD.dbo.H_LIQUIDACIONES LC on M.Cuenta = LC.afiliado WHERE LC.id_categoria_liquidacion in ('AU', 'BA', 'BE','GR','PP','RE') and LC.id_periodo = @PERIODO_ANTERIOR GROUP BY m.Id_Periodo, LC.afiliado -------------------------------------------------------------------------
UPDATE ValGril SET Valor_Grilla = (Valor_Grilla / Contador_Grupo) FROM ValGril M JOIN Cuentas_REPETIDAS LC on M.Afiliado = LC.Cuenta -------------------------------------------------------------------------
------------------------------------------------------------------------- UPDATE altas SET Valor_Grilla = V.Valor_Grilla, Valor_Grilla_Prorrateado = V.Valor_Grilla FROM altas A JOIN ValGril V on V.Afiliado = A.Cuenta -------------------------------------------------------------------------
update altas set Valor_Grilla_Prorrateado = (Valor_Grilla_Prorrateado/Cant_Grupo_Familiar) from altas
------------------------------------------------------------------------- update altas set Valor_Grilla = 0 where Valor_Grilla is null update altas set Valor_Grilla_Prorrateado = 0 where Valor_Grilla_Prorrateado is null -------------------------------------------------------------------------
/* Actualizo Valor Aporte Legal */ SELECT m.Id_Periodo, LC.afiliado as Afiliado, SUM(LC.total_concepto) as Aporte_Legal INTO ValApor FROM altas M JOIN DW_SALUD.dbo.H_LIQUIDACIONES LC on M.Cuenta = LC.afiliado WHERE LC.id_categoria_liquidacion in ('AL') AND LC.id_periodo = @PERIODO_ANTERIOR GROUP BY m.Id_Periodo, LC.afiliado
-------------------------------------------------------------------------
UPDATE ValApor SET Aporte_Legal = (Aporte_Legal / Contador_Grupo) FROM ValApor M JOIN Cuentas_REPETIDAS LC on M.Afiliado = LC.Cuenta
------------------------------------------------------------------------- update altas set Valor_Aporte_Legal = V.Aporte_Legal, Valor_Aporte_Legal_Prorrateado = V.Aporte_Legal from altas A inner join ValApor V on V.Afiliado = A.Cuenta
-------------------------------------------------------------------------
update altas set Valor_Aporte_Legal_Prorrateado = (Valor_Aporte_Legal_Prorrateado/Cant_Grupo_Familiar) from altas
-------------------------------------------------------------------------
update altas set Valor_Aporte_Legal = 0 where Valor_Aporte_Legal is null update altas set Valor_Aporte_Legal_Prorrateado = 0 where Valor_Aporte_Legal_Prorrateado is null
/* Actualizo Valor Aporte Adicional */ update A set Valor_Aporte_Adicional = (AL.Valor_Grilla - AL.Valor_Aporte_Legal) from Altas A inner join Altas AL on A.Cuenta = AL.Cuenta
update A set Valor_Aporte_Adicional_Prorrateado = (AL.Valor_Grilla_Prorrateado - AL.Valor_Aporte_Legal_Prorrateado) from Altas A inner join Altas AL on A.Cuenta = AL.Cuenta
