/* Actualizo la tabla de D_PUESTOS_CASCADA_COMERCIAL */
INSERT INTO D_PUESTOS_CASCADA_COMERCIAL (id_puesto_cascada_comercial, de_puesto_cascada_comercial, id_tipo_puesto_cascada_comercial) SELECT DISTINCT IDPUESTO , PUESTO, IDTIPOPUESTO FROM [HOST03].[HOST03].[COMERCIAL].[PUESTOS] WHERE IDPUESTO not in (SELECT id_puesto_cascada_comercial FROM D_PUESTOS_CASCADA_COMERCIAL)
/* Actualiza la tabla D_PUESTOS_CASCADA_COMERCIAL tomando los datos de la as400 */
Update D_PUESTOS_CASCADA_COMERCIAL set id_puesto_cascada_comercial = as400.IDPUESTO, de_puesto_cascada_comercial = as400.PUESTO, id_tipo_puesto_cascada_comercial = as400.IDTIPOPUESTO FROM D_PUESTOS_CASCADA_COMERCIAL D inner join [HOST03].[HOST03].[COMERCIAL].[PUESTOS] as400 on as400.IDPUESTO = D.id_puesto_cascada_comercial
