/* Actualizo la tabla de D_TIPOS_PUESTOS_CASCADA_COMERCIAL */
INSERT INTO D_TIPOS_PUESTOS_CASCADA_COMERCIAL (id_tipo_puesto_cascada_comercial, de_tipo_puesto_cascada_comercial) SELECT DISTINCT IDTIPOPUESTO , TIPOPUESTO FROM [HOST03].[HOST03].[COMERCIAL].[TIPOS_PUESTOS] WHERE IDTIPOPUESTO not in (SELECT id_tipo_puesto_cascada_comercial FROM D_TIPOS_PUESTOS_CASCADA_COMERCIAL)
----- Actualiza la tabla D_TIPOS_PUESTOS_CASCADA_COMERCIAL tomando los datos de la as400 Update D_TIPOS_PUESTOS_CASCADA_COMERCIAL set id_tipo_puesto_cascada_comercial = as400.IDTIPOPUESTO, de_tipo_puesto_cascada_comercial = as400.TIPOPUESTO FROM D_TIPOS_PUESTOS_CASCADA_COMERCIAL D inner join [HOST03].[HOST03].[COMERCIAL].[TIPOS_PUESTOS] as400 on as400.IDTIPOPUESTO = D.id_tipo_puesto_cascada_comercial
