SET DATEFORMAT 'YMD'
Declare @Periodo Int Set @Periodo = ?
/* Borra AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS */ DELETE FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS WHERE id_periodo = @Periodo
/* Crea Temporal #DebitoAutomatico como previa a insertar en la AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS*/ select a11.id_periodo, a11.id_acreedor, a11.signo_debito_credito, a11.id_producto, a11.id_origen_debito, sum((a11.importe_debito * a11.signo_debito_credito)) importe, (CASE when a11.id_producto in (43,44,46) then 2 else 1 end) as id_compania INTO #DebitoAutomatico from H_DEBITOS a11 join D_PRODUCTOS a12 on (a11.id_producto = a12.id_producto) join D_PLANES a13 on (a11.id_plan_producto = a13.id_plan_producto and a11.id_producto = a13.id_producto) join D_DEBITO_CREDITO a14 on (a11.signo_debito_credito = a14.signo_debito_credito) where (a12.Id_Clase_Producto in ('S ') and a13.id_subrubro_ley in (1, 2, 3, 5, 6, 7) and a12.id_segmento not in (3) and not ((a11.id_producto = 16 and a11.id_plan_producto = 'CC ') or (a11.id_producto=16 and a11.id_plan_producto='CM ') or (a11.id_producto=16 and a11.id_plan_producto='CS1 ') or (a11.id_producto=16 and a11.id_plan_producto='CS100') or (a11.id_producto=4 and a11.id_plan_producto='M ') or (a11.id_producto=16 and a11.id_plan_producto='M ') or (a11.id_producto=20 and a11.id_plan_producto='M ') or (a11.id_producto=14 and a11.id_plan_producto='Z ') or (a11.id_producto=20 and a11.id_plan_producto='Z ') or (a11.id_producto=1 and a11.id_plan_producto='C ') or (a11.id_producto=4 and a11.id_plan_producto='PMO ') or (a11.id_producto=35 and a11.id_plan_producto='PMO ') or (a11.id_producto=4 and a11.id_plan_producto='Z ')) and a11.id_destinatario_debito in ('A') and a11.id_periodo = @Periodo) group by a11.id_periodo, a11.id_acreedor, a11.signo_debito_credito, a11.id_producto, a11.id_origen_debito
/* Inserta en la AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS */
-- Actualiza Creditos Automáticos Administrativos. INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 23 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (-1) and id_origen_debito = 'A'
-- Actualiza Creditos Automáticos Tecnicos. INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 25 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (-1) and id_origen_debito = 'T'
-- Actualiza Creditos Automáticos Medicos INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 27 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (-1) and id_origen_debito = 'M'
-- Actualiza Debitos Automáticos INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 22 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (1) and id_origen_debito = 'A'
-- Actualiza Debitos Automáticos Tecnicos. INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 24 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (1) and id_origen_debito = 'T'
-- Actualiza Debitos Automáticos Medicos. INSERT INTO DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_AUTOMATICOS (ID_Periodo, id_acreedor, id_tipo_circuito, id_producto, importe_debito, id_compania) SELECT distinct Id_Periodo, id_acreedor, 26 as id_tipo_circuito, id_producto, importe, id_compania FROM #DebitoAutomatico WHERE signo_debito_credito in (1) and id_origen_debito = 'M'
/* Borra Tablas Auxiliares */ drop table #DebitoAutomatico
