Declare @Periodo Int Set @Periodo = ? Declare @Periodo_Anterior Int SET @Periodo_Anterior = dbo.Periodo3MesesAtras(CAST(CAST(@Periodo as CHAR(6))+'01' as datetime))
/* Actualiza marca procesado para trabajar con las claves que son solo del periodo ingresado */ UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET marca_procesado = 0 WHERE id_periodo_recepcion = @Periodo
/* Actualiza los Tipos de Circuito para cada caso. Aqui solo tenemos Administrativos (fijado por definicion para los acreedores de capitas) y Sin Datos. */ UPDATE AUX_H_CIRCUITO_DEBITOS_MANUALES set id_tipo_circuito = (CASE -- Debitos when Tipo_comprobante in ('DMAN','DMAC') and id_periodo_recepcion = @Periodo and id_origen_debito = 'A' then 14 when Tipo_comprobante in ('DMAN','DMAC') and id_periodo_recepcion = @Periodo and id_origen_debito = 'X' then 20 -- Creditos when Tipo_comprobante in ('CMAN') and id_periodo_recepcion = @Periodo and id_origen_debito = 'A' then 17 when Tipo_comprobante in ('CMAN') and id_periodo_recepcion = @Periodo and id_origen_debito = 'X' then 21 when Tipo_comprobante in ('CMAR') and id_periodo_recepcion = @Periodo then 31 else id_tipo_circuito end)
/* Actualiza el Nro de Factura para luego hacer relación con la Facturación */ -- AMS UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET nro_factura_acree = W_V50LEF+W_V50CHF FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES A JOIN HOST03.HOST03.AMSPROVD.V550FDECEX X on A.Nro_Talonario = X.W_V50TAD and A.Id_Acreedor = X.W_V50NPD and A.Tipo_Comprobante collate SQL_Latin1_General_CP850_CI_AI = X.W_V50TCD and A.Numero_Comprobante = X.W_V50NCD WHERE id_periodo_recepcion = @Periodo
-- SMP UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET nro_factura_acree = W_V50LEF+W_V50CHF FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES A JOIN HOST03.HOST03.SMPPROVD.V550FDECEX X on A.Nro_Talonario = X.W_V50TAD and A.Id_Acreedor = X.W_V50NPD and A.Tipo_Comprobante collate SQL_Latin1_General_CP850_CI_AI = X.W_V50TCD and A.Numero_Comprobante = X.W_V50NCD WHERE id_periodo_recepcion = @Periodo
-- OSP UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET nro_factura_acree = W_V50LEF+W_V50CHF FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES A JOIN HOST03.HOST03.OSPPROVD.V550FDECEX X on A.Nro_Talonario = X.W_V50TAD and A.Id_Acreedor = X.W_V50NPD and A.Tipo_Comprobante collate SQL_Latin1_General_CP850_CI_AI = X.W_V50TCD and A.Numero_Comprobante = X.W_V50NCD WHERE id_periodo_recepcion = @Periodo
-- STAFF UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET nro_factura_acree = W_V50LEF+W_V50CHF FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES A JOIN HOST03.HOST03.STFPROVD.V550FDECEX X on A.Nro_Talonario = X.W_V50TAD and A.Id_Acreedor = X.W_V50NPD and A.Tipo_Comprobante collate SQL_Latin1_General_CP850_CI_AI = X.W_V50TCD and A.Numero_Comprobante = X.W_V50NCD WHERE id_periodo_recepcion = @Periodo
/* Actualiza el id_periodo con el periodo que le corresponde a cada caso buscado y encontrado en Facturación */ -- Esto hace que se "desparramen" los periodos por varios periodos. UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET Id_Periodo = (CASE when X.Id_Periodo < @Periodo_Anterior then A.Id_Periodo else X.Id_Periodo end), Nro_Cursograma_Facturacion = X.Nro_Cursograma FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES A JOIN DW_SALUD.dbo.AUX_H_CIRCUITO_FACTURACION X on A.nro_factura_acree = X.nro_factura_acree and A.Id_Acreedor = X.Id_Acreedor WHERE X.Nro_Cursograma <> 0 and A.id_periodo_recepcion = @Periodo
UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES SET id_periodo = id_periodo_recepcion where id_periodo is null and id_periodo_recepcion = @Periodo
/* Borra de los debitos manuales los casos que provienen de Cursogramas Anulados en Facturación */ DELETE DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES FROM DW_SALUD.dbo.AUX_H_CIRCUITO_DEBITOS_MANUALES M JOIN HOST03.HOST03.DESARROLLO.CUP004 C on C.CUC_NUM = M.nro_cursograma_facturacion WHERE ECA_COD in ('A') and M.nro_factura_acree not in ('X') and M.nro_cursograma_facturacion not in (9999999) and id_periodo_recepcion = @Periodo

/* Actualiza AUX_H_CIRCUITO_DEBITOS_MANUALES */

