/* Borra y crea tabla DW_SALUD.dbo.AUX_AUX_H_CIRCUITO_REFACTURACION para luego crea una con cero registros para tener de estructura. */
Declare @Periodo Int Set @Periodo = ?
DELETE FROM DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION WHERE id_periodo_recepcion = @Periodo

Declare @Periodo_Recepcion Int SET @Periodo_Recepcion = ? Declare @Periodo_Anterior Int SET @Periodo_Anterior = dbo.Periodo3MesesAtras(CAST(CAST(@Periodo_Recepcion as CHAR(6))+'01' as datetime))
-- Actualiza id_tipo_circuito segun Tipo_Refacturacion UPDATE AUX_H_CIRCUITO_REFACTURACION set id_tipo_circuito = (CASE when Tipo_Refacturacion in ('A') and id_periodo_recepcion = @Periodo_Recepcion then 2 when Tipo_Refacturacion in ('M') and id_periodo_recepcion = @Periodo_Recepcion then 3 when Tipo_Refacturacion in (' ') and id_periodo_recepcion = @Periodo_Recepcion then 4 when Tipo_Refacturacion in ('D') and id_periodo_recepcion = @Periodo_Recepcion then 13 end) WHERE id_periodo_recepcion = @Periodo_Recepcion
/* Actualiza marca procesado para trabajar con las claves que son solo del periodo ingresado */ UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION SET marca_procesado = 0 WHERE id_periodo_recepcion = @Periodo_Recepcion
/* Actualiza Marca de S y N para "Refacturacion Salud" */
-- Relaciona la informacion con la V600FAUDCI. Todo lo que encuentre en esta relación pertenecece a SALUD por ende actualizamos el campo Refacturacion_Salud en S, de lo contrario el campo queda vacio. UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION SET Refacturacion_Salud = 'S' FROM HOST03.HOST03.SMPDATD.V600FAUDCI T join DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION R on R.Nro_Talonario = T.W_V600_TALOA and R.Tipo_Comprobante collate SQL_Latin1_General_CP850_CI_AI = T.W_V600_TCOMA and R.numero_comprobante = T.W_V600_NCOMA WHERE id_periodo_recepcion = @Periodo_Recepcion
UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION SET Refacturacion_Salud = 'N' WHERE Refacturacion_Salud in (' ') and id_periodo_recepcion = @Periodo_Recepcion
/* Actualiza campo CusogramaEnTablero para ver cual Cursograma encuentra en las H del Tablero */
-- Ambulatorio. UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION set CursogramaDWH = 'S' FROM DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION R JOIN DW_SALUD.dbo.H_ORD_AMB_DET C on C.nro_cursograma = R.numero_comprobante WHERE id_periodo_recepcion = @Periodo_Recepcion and id_subsidio_supra not in ('S') and R.marca_procesado = 0
-- Internaciones. UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION set CursogramaDWH = 'S' FROM DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION R JOIN DW_SALUD.dbo.H_ORD_INT_DET C on C.nro_cursograma = R.numero_comprobante WHERE id_periodo_recepcion = @Periodo_Recepcion and R.marca_procesado = 0
-- Marca en N de NO los casos que no se encuentran. UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION set CursogramaDWH = 'N' WHERE id_periodo_recepcion = @Periodo_Recepcion and CursogramaDWH not in ('S') and marca_procesado = 0
/* Actualiza Fecha de Recepcion del cursograma Original para luego hacer el corte por esta información. */
UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION SET id_periodo = (CASE when T.Id_Periodo < @Periodo_Anterior then R.Id_Periodo else T.Id_Periodo end) FROM DW_SALUD.dbo.AUX_H_CIRCUITO_FACTURACION T JOIN DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION R on R.Nro_Cursograma = T.Nro_Cursograma WHERE R.Nro_Cursograma <> 0 and R.marca_procesado = 0 and R.id_periodo_recepcion = @Periodo_Recepcion
UPDATE DW_SALUD.dbo.AUX_H_CIRCUITO_REFACTURACION SET id_periodo = id_periodo_recepcion where id_periodo is null and id_periodo_recepcion = @Periodo_Recepcion
/* Pasa los CMAR a Refacturación desde Débitos Manuales. */
-- Actualiza en Refacturación. INSERT INTO AUX_H_CIRCUITO_REFACTURACION (id_periodo, Origen_Refacturacion, Nro_Cursograma, Tipo_Acreedor, Id_Acreedor, Nro_Talonario, Tipo_Comprobante, Numero_Comprobante, Fecha_Comprobante, Fecha_Recepcion, Marca_Refacturacion, Tipo_Refacturacion, importe, Refacturacion_Salud, CursogramaDWH, Signo, id_periodo_recepcion, marca_procesado, id_tipo_circuito, id_compania, id_producto) select id_periodo, 'R' as Origen_Refacturacion, Nro_Cursograma, Tipo_Acreedor, Id_Acreedor, Nro_Talonario, Tipo_Comprobante, Numero_Comprobante, Fecha_Comprobante, Fecha_Recepcion, 'N' as Marca_Refacturacion, '' as Tipo_Refacturacion, importe, 'N' as Refacturacion_Salud, nro_cursograma_facturacion as CursogramaDWH, 'I' as Signo, id_periodo_recepcion, 0 as marca_procesado, id_tipo_circuito, id_compania, id_producto from AUX_H_CIRCUITO_DEBITOS_MANUALES where id_periodo_recepcion = @Periodo_Recepcion and Tipo_Comprobante = 'CMAR'
-- Borra en Manuales. DELETE FROM AUX_H_CIRCUITO_DEBITOS_MANUALES where id_periodo_recepcion = @Periodo_Recepcion and Tipo_Comprobante = 'CMAR'
