/* Declara Variables. */
DECLARE @Fecha_Desde CHAR(8) = ?
DECLARE @Periodo INT = SUBSTRING(@Fecha_Desde,1,6)
/* Actualiza H_DEBITOS_DIARIOS. */
DELETE FROM H_DEBITOS_DIARIOS where id_periodo_cierre = @Periodo
INSERT INTO H_DEBITOS_DIARIOS (id_persona, id_periodo, fecha_recepcion, id_periodo_cierre, fecha_cierre, id_posicion_barra, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, fecha_prestacion, id_reintegro, id_acreedor, id_tipo_prestador, id_producto, id_subproducto, id_plan_producto, id_usuario_auditor, id_concepto_debito, id_tipo_orden, nro_orden, id_origen_debito, id_tipo_debito, id_origen_prestacion, importe_debito) select id_persona, SUBSTRING(fecha_recepcion,1,6) as id_periodo, CAST(fecha_recepcion as datetime) as fecha_recepcion, SUBSTRING(fecha_cierre,1,6) as id_periodo_cierre, CAST(fecha_cierre as datetime) as fecha_cierre, id_posicion_barra, nro_cursograma, Nro_Lote, nro_secuencia, id_prestacion, CAST(fecha_prestacion as datetime) as fecha_prestacion, id_reintegro, id_acreedor, ISNULL(id_tipo_prestador,'P') , a11.id_producto, id_subproducto, a11.id_plan_producto, id_usuario_auditor, a11.id_concepto_debito, id_tipo_orden, nro_orden, id_origen_debito, id_tipo_debito, id_origen_prestacion, importe_debito from AUX_H_DEBITOS_DIARIOS a11 join D_PLANES a12 on (a11.id_plan_producto = a12.id_plan_producto and a11.id_producto = a12.id_producto) join D_PRODUCTOS a13 on (a12.id_producto = a13.id_producto) join D_CONCEPTO_DEBITO a14 on (a11.id_concepto_debito = a14.id_concepto_debito) where (a13.Id_Clase_Producto in ('S ') and a12.Id_SubRubro_Ley in (1, 2, 3, 5, 6, 7) and not ((a11.id_producto = 16 and a11.id_plan_producto = 'CC ') or (a11.id_producto=16 and a11.id_plan_producto='CM ') or (a11.id_producto=16 and a11.id_plan_producto='CS1 ') or (a11.id_producto=16 and a11.id_plan_producto='CS100') or (a11.id_producto=4 and a11.id_plan_producto='M ') or (a11.id_producto=16 and a11.id_plan_producto='M ') or (a11.id_producto=20 and a11.id_plan_producto='M ') or (a11.id_producto=14 and a11.id_plan_producto='Z ') or (a11.id_producto=20 and a11.id_plan_producto='Z ') or (a11.id_producto=1 and a11.id_plan_producto='C ') or (a11.id_producto=4 and a11.id_plan_producto='Z ')) and a13.id_segmento not in (3) and SUBSTRING(fecha_cierre,1,6) = @Periodo)

/* Actualiza la tabla de PLANES */
INSERT INTO D_PLANES (id_producto, id_plan_producto, de_planes, de_plan_reducida, orden_plan, id_plan_activo, visualizacion_web, Id_Plan_Agrupado, Id_Rubro_Salud, Id_Tipo_Planes, Id_Subrubro_Ley, Id_Plan_Agrupado_Convenio, Id_SubRubro_Ley_Agrupado) SELECT DISTINCT id_producto, id_plan_producto, 'No definido' as de_planes, ' ' as de_plan_reducida, 999 as orden_plan, 'NO' as id_plan_activo, 0 as visualizacion_web, 999 as Id_Plan_Agrupado, ' ' as Id_Rubro_Salud, null as Id_Tipo_Planes, 0 as Id_Subrubro_Ley, 999 as Id_Plan_Agrupado_Convenio, 0 as Id_SubRubro_Ley_agrupado FROM AUX_H_DEBITOS_DIARIOS D where not exists (select P.id_producto, P.id_plan_producto from D_PLANES P where D.id_producto = P.id_producto and D.id_plan_producto = P.id_plan_producto)
/* Actualizo la tabla de ACREEDOR */ INSERT INTO D_ACREEDOR (id_acreedor, De_Acreedor, De_ACreedor_reduc, id_categoria_acreedor, id_provincia, Id_localidad, subcodpos, id_posicion_acreedor, id_Tipo_prestador, simulador_convenio, evolucion_pareto, orden_pareto) select id_acreedor, MAX('F/D') as De_Acreedor, MAX('F/D') as De_ACreedor_reduc, MAX(99999) as id_categoria_acreedor, MAX(9) as id_provincia, MAX(9999) as Id_localidad, MAX(99) as subcodpos, MAX(999) as id_posicion_acreedor, id_Tipo_prestador, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto from AUX_H_DEBITOS_DIARIOS H where NOT EXISTS (Select H.id_acreedor, H.id_Tipo_Prestador from d_acreedor D WHERE H.id_acreedor = D.id_acreedor AND H.id_tipo_prestador = D.id_tipo_prestador) group by id_acreedor, id_Tipo_prestador
/* Actualizo la tabla de PRESTACION */ INSERT INTO D_PRESTACION (id_prestacion, id_nomenclador, de_codigo_prestacion ,de_prestacion, id_capitulo, id_subcapitulo, id_seccion, id_seguir_prestacion, id_grupo_prestacion, id_subgrupo_prestacion, id_accion_farmacologica, id_servicio_prest, id_catalogada, id_cod_resolucion, id_nivel_capitacion, id_complejidad, id_provision_prest, id_rubro_ind_consumo, id_prestacion_oncologia, id_marca_recupero_sur, simulador_convenio, evolucion_pareto, orden_pareto) SELECT DISTINCT id_prestacion, SUBSTRING(id_prestacion,1,2) as id_nomenclador, SUBSTRING(id_prestacion,3,8) as de_codigo_prestacion, 'F/D' as de_prestacion, 99 as id_capitulo, 99 as id_subcapitulo, 99 as id_seccion, 'N' as id_seguir_prestacion, 99 as id_grupo_prestacion, 99 as id_subgrupo_prestacion, 9999 as id_accion_farmacologica, 99 as id_servicio_prest, 'S' as id_catalogada, 9 as id_cod_resolucion, 999 as id_nivel_capitacion, 999 as id_complejidad, 0 as id_provision_prest, ' ' as id_rubro_ind_consumo, 'N' as id_prestacion_oncologia, 0 as id_marca_recupero_sur, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto FROM DW_SALUD.dbo.AUX_H_DEBITOS_DIARIOS WHERE id_prestacion not in (SELECT id_prestacion FROM d_prestacion)
/* Actualiza D_USUARIO_AUDITOR */
INSERT INTO D_USUARIO_AUDITOR (id_usuario_auditor, de_usuario_auditor) SELECT distinct id_usuario_auditor, id_usuario_auditor as de_usuario_auditor FROM AUX_H_DEBITOS_DIARIOS WHERE id_usuario_auditor not in (select id_usuario_auditor from D_USUARIO_AUDITOR)
/* Actualiza D_CONCEPTO_DEBITO. */ INSERT INTO D_CONCEPTO_DEBITO ([id_concepto_debito], [de_concepto_debito], [concepto_deb_cred]) SELECT W47CON as id_concepto_debito, W47DES as de_concepto_debito, W47CCT as concepto_deb_cred FROM HOST03.HOST03.SMPLIBD.T47FCON WHERE W47CON not in (select id_concepto_debito from D_CONCEPTO_DEBITO)
update D_CONCEPTO_DEBITO set de_concepto_debito = W47DES, concepto_deb_cred = W47CCT FROM AS400.HOST03.SMPLIBD.T47FCON T join D_CONCEPTO_DEBITO D on T.W47CON = d.id_concepto_debito where de_concepto_debito <> W47DES COLLATE SQL_Latin1_General_CP850_CI_AI or concepto_deb_cred <> W47CCT COLLATE SQL_Latin1_General_CP850_CI_AI
/* Actualiza D_ORIGEN_DEBITO. */
INSERT INTO D_ORIGEN_DEBITO (id_origen_debito, de_origen_debito) SELECT distinct id_origen_debito, 'F/D' as de_origen_debito FROM AUX_H_DEBITOS_DIARIOS WHERE id_origen_debito not in (select id_origen_debito from D_ORIGEN_DEBITO)
/* Actualiza D_TIPO_DEBITO. */
INSERT INTO D_TIPO_DEBITO (id_tipo_debito, de_tipo_debito) SELECT distinct id_tipo_debito, 'F/D' as de_tipo_debito FROM AUX_H_DEBITOS_DIARIOS WHERE id_tipo_debito not in (select id_tipo_debito from D_TIPO_DEBITO)
