declare @Periodo int declare @PeriodoAnterior int set @Periodo = (select MAX(id_periodo) from H_TRANSACCIONES_CABECERA) set @PeriodoAnterior = dbo.PeriodosAnteriores(@periodo,3)
/* actualiza por periodo */ update H_TRANSACCIONES_CABECERA set id_compania = R.id_compania from H_TRANSACCIONES_CABECERA T join H_COMER_STOCK_AFILIA S on (T.id_persona = S.id_persona and T.id_periodo = S.id_periodo) join D_PLANES P on (S.id_producto = P.id_producto and S.id_plan_producto = P.id_plan_producto) join D_SUBRUBRO_LEY SR on (P.id_SubRubro_ley = SR.Id_SubRubro_Ley) join D_RUBRO_LEY R on (SR.id_Rubro_Ley = R.Id_Rubro_ley) where T.id_persona <> 0 and T.id_periodo between @PeriodoAnterior and @Periodo and T.id_compania is null
/* si no lo encuentra en ese periodo busca el maximo de stock */ select T.id_persona, MAX(S.id_periodo) as max_periodo_stock, T.id_periodo into #MAXPERSTOCK from H_TRANSACCIONES_CABECERA T join H_COMER_STOCK_AFILIA S on (S.id_persona = T.id_persona) where T.id_periodo between @PeriodoAnterior and @Periodo and id_compania is null and T.id_persona <> 0 group by T.id_periodo, T.id_persona
update H_TRANSACCIONES_CABECERA set id_compania = R.id_compania from H_TRANSACCIONES_CABECERA T join #MAXPERSTOCK M on (M.id_persona = T.id_persona and T.id_periodo = M.id_periodo) join H_COMER_STOCK_AFILIA S on (T.id_persona = S.id_persona and M.max_periodo_stock = S.id_periodo) join D_PLANES P on (S.id_producto = P.id_producto and S.id_plan_producto = P.id_plan_producto) join D_SUBRUBRO_LEY SR on (P.id_SubRubro_ley = SR.Id_SubRubro_Ley) join D_RUBRO_LEY R on (SR.id_Rubro_Ley = R.Id_Rubro_ley) where T.id_persona <> 0 and T.id_periodo between @PeriodoAnterior and @Periodo and T.id_compania is null
/* por definicion del sector de conectividad, los registros con persona en 0 van a la compania 1 Sancor Salud ademas de esto, existen casos en los que el asociado se cargo con fecha de carga posterior a la fecha de vigencia, estos casos tambien van a Sancor Salud*/ update H_TRANSACCIONES_CABECERA set id_compania = 1 where id_compania is null and id_periodo between @PeriodoAnterior and @Periodo
drop table #MAXPERSTOCK
