/* Actualiza Compañia en H_TRANSACCIONES_PRESTACIONES */
update H_TRANSACCIONES_PRESTACIONES set id_compania = C.id_compania from H_TRANSACCIONES_PRESTACIONES T join H_TRANSACCIONES_CABECERA C on T.ID = C.ID
/* por definicion del sector de conectividad, los registros con persona en 0 van a la compania 1 Sancor Salud ademas de esto, existen casos en los que el asociado se cargo con fecha de carga posterior a la fecha de vigencia, estos casos tambien van a Sancor Salud*/
update H_TRANSACCIONES_PRESTACIONES set id_compania = 1 where id_compania is null

declare @periodo3 int set @periodo3 = (select min(a.id_periodo) from (Select distinct top 3 Id_periodo From H_TRANSACCIONES_PRESTACIONES group by id_periodo Order by (id_periodo) desc) a)
/* Actualizo la tabla de PLANES */ INSERT INTO D_PLANES (id_producto, id_plan_producto, de_planes, de_plan_reducida, orden_plan, id_plan_activo, visualizacion_web, Id_Plan_Agrupado, Id_Rubro_Salud, Id_Tipo_Planes, Id_Subrubro_Ley, Id_Plan_Agrupado_Convenio, Id_SubRubro_Ley_Agrupado) SELECT DISTINCT id_producto, id_plan_producto, 'No definido' as de_planes, ' ' as de_plan_reducida, 999 as orden_plan, 'NO' as id_plan_activo, 0 as visualizacion_web, 999 as Id_Plan_Agrupado, ' ' as Id_Rubro_Salud, null as Id_Tipo_Planes, 0 as Id_Subrubro_Ley, 0 as Id_Plan_Agrupado_Convenio, 0 as Id_SubRubro_Ley_agrupado from H_TRANSACCIONES_PRESTACIONES a11 where not EXISTS (select * from d_planes a12 where a11.id_producto = a12.id_producto and a11.id_plan_producto = a12.id_plan_producto) and id_periodo >= @periodo3
/* Actualizo la tabla de PRESTACION */ INSERT INTO d_prestacion ([id_prestacion] ,[id_nomenclador] ,[de_codigo_prestacion] ,[de_prestacion] ,[id_capitulo] ,[id_subcapitulo] ,[id_seccion] ,[id_seguir_prestacion] ,[id_grupo_prestacion] ,[id_subgrupo_prestacion] ,[id_accion_farmacologica] ,[id_servicio_prest] ,[id_catalogada] ,[id_cod_resolucion] ,[id_nivel_capitacion] ,[id_complejidad] ,[id_provision_prest] ,[id_rubro_ind_consumo] ,[id_prestacion_oncologia] ,[id_marca_recupero_sur] ,[simulador_convenio] ,[evolucion_pareto] ,[orden_pareto]) SELECT DISTINCT id_prestacion, SUBSTRING(id_prestacion, 1 , 2 ) as id_nomenclador, SUBSTRING(id_prestacion, 3 , 8 ) as de_codigo_prestacion, 'F/D' as de_prestacion, 99 as id_capitulo, 99 as id_subcapitulo, 99 as id_seccion, 'N' as id_seguir_prestacion, 99 as id_grupo_prestacion, 99 as id_subgrupo_prestacion, 9999 as id_accion_farmacologica, 99 as id_servicio_prest, 'S' as id_catalogada, 9 as id_cod_resolucion, 999 as id_nivel_capitacion, 999 as id_complejidad, 0 as id_provision_prest, ' ' as id_rubro_ind_consumo, 'N' as id_prestacion_oncologia, 0 as id_marca_recupero_sur, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto FROM H_TRANSACCIONES_PRESTACIONES WHERE id_prestacion not in (SELECT id_prestacion FROM d_prestacion)
/* Actualizo la tabla de ACREEDOR */ INSERT INTO D_ACREEDOR (id_acreedor, De_Acreedor, De_ACreedor_reduc, id_categoria_acreedor, id_provincia, Id_localidad, subcodpos, id_posicion_acreedor, id_Tipo_prestador, simulador_convenio, evolucion_pareto, orden_pareto) select id_acreedor, MAX('F/D') as De_Acreedor, MAX('F/D') as De_ACreedor_reduc, MAX(99999) as id_categoria_acreedor, MAX(9) as id_provincia, MAX(9999) as Id_localidad, MAX(99) as subcodpos, MAX(999) as id_posicion_acreedor, id_Tipo_prestador, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto from H_TRANSACCIONES_PRESTACIONES h where id_periodo in (Select id_periodo from aux_periodo_mes_borra) and NOT EXISTS (Select * from d_acreedor D WHERE H.id_acreedor = D.id_acreedor AND H.id_tipo_prestador = D.id_tipo_prestador) group by h.id_acreedor , h.id_tipo_prestador /* Actualizo la tabla de PRODUCTOS */ INSERT INTO d_productos (id_producto, id_clase_producto, id_segmento, id_rubro_salud, de_producto, id_tip_segmento, id_tip_producto) SELECT DISTINCT id_producto, 'X' as id_clase_producto, 0 as id_segmento, 'X' as id_rubro_salud, 'No definido' as de_producto, 'X' as id_tip_segmento, 'X' as id_tip_producto FROM H_TRANSACCIONES_PRESTACIONES WHERE id_producto not in (SELECT id_producto FROM d_productos)
/* Actualizo la tabla de PROFESIONAL */
INSERT INTO d_profesional (id_profesional, de_profesional, id_provincia, id_localidad, id_posicion_profesional,id_posicion_relacionada, id_tipo_prestador) SELECT DISTINCT id_profesional, 'F/D' as de_profesional, 9 as id_provincia, 9999 as id_localidad, 99 as id_posicion_profesional, 99 as id_posicion_relacionada, id_tipo_profesional_preescribiente FROM H_TRANSACCIONES_PRESTACIONES WHERE id_profesional not in (SELECT id_profesional FROM d_profesional)
