--Calcula el periodo de 13 meses antes DECLARE @Periodo13 INT SET @Periodo13 = dbo.PeriodosAnteriores((SELECT MAX(id_periodo) FROM H_TRANSACCIONES_PRESTACIONES),13)
/*setea en 0 la columna de la cup para los ultimos 13 meses*/ UPDATE H_TRANSACCIONES_PRESTACIONES SET cant_prest_cup = 0, imp_prest_cup = 0 from H_TRANSACCIONES_PRESTACIONES where id_periodo >= @Periodo13 and id_tipo_transaccion_metodo <> 3
/* Actualiza datos provenientes de la CUP008 */ UPDATE H_TRANSACCIONES_PRESTACIONES SET cant_prest_cup = IsNull(sumaPrest,0), imp_prest_cup = IsNull(sumaImp,0) FROM H_TRANSACCIONES_PRESTACIONES P INNER JOIN (SELECT CUP_TOR, CUP_NOR, id_prestacion, SUM(CUP_PAD) AS sumaPrest, SUM(CUP_IMP) AS sumaImp FROM TMP_TRANSACCIONES_PRESTACIONES GROUP BY CUP_TOR, CUP_NOR, id_prestacion) AS T ON (T.CUP_TOR=P.id_tipo_orden_transaccion and T.CUP_NOR=P.numero_orden_transaccion and T.id_prestacion=P.id_prestacion) where id_tipo_transaccion_metodo <> 3
-- Inicializa en cero el campo cant_prest_cons UPDATE H_TRANSACCIONES_PRESTACIONES SET cant_prest_cons = 0 WHERE cant_prest_cons IS NULL
/* Setea en 0 los campos en nulo*/ update H_TRANSACCIONES_PRESTACIONES set cant_prest_pag = 0 where cant_prest_pag is null
update H_TRANSACCIONES_PRESTACIONES set cant_prest_cup = 0 where cant_prest_cup is null
/* Inicializa en 0 datos de la cup. */
UPDATE H_TRANSACCIONES_PRESTACIONES SET cant_prest_cup = 0, imp_prest_cup = 0 WHERE id_periodo >= @Periodo13 AND cant_prest_cons > 0 /*Actualiza la cup en 0 siempre que en consumo haya valor */
/* Actualiza prestaciones e importe pagados en H_TRANSACCIONES_PRESTACIONES*/ UPDATE H_TRANSACCIONES_PRESTACIONES SET cant_prest_pag = ISNULL(cant_prest_cons+cant_prest_cup, 0), imp_prest_pag = ISNULL(imp_prest_cup + imp_prest_cons,0)
