/*Si tiene mas de un cursograma divido el importe solicitado por la cantidad para que la suma de estos sea el importe real*/ update aux_H_CONSUMOS_REINTEGROS set imp_solicitado_reintegro = imp_solicitado_reintegro * cant_reintegros where cant_reintegros < 1
