/* Actualizo la información existente */ UPDATE d_entidades SET de_entidad = a12.de_entidad, id_zona_comarcal = a12.id_zona_comarcal, fecha_fin_vigencia = REPLACE(a12.fecha_fin_vigencia,'00000000','') FROM AUX_D_ENTIDADES a12 JOIN d_entidades E on E.id_entidad = a12.id_entidad
/* Creo la información faltante */ INSERT INTO d_entidades (id_entidad, de_entidad, id_zona_comarcal, fecha_fin_vigencia, id_empresa_entidad, Id_Tipo_Entidad_entidad, horario_atencion, referente_1, referente_2, calle_entidad, nro_puerta_entidad, piso_entidad, departamento_entidad, id_localidad_entidad, caracteristica_te_entidad, nro_telefono_entidad, mail_entidad, id_categoria_entidad) SELECT id_entidad as id_entidad, de_entidad as de_entidad, id_zona_comarcal as id_zona_comarcal, REPLACE(fecha_fin_vigencia,'00000000','') as fecha_fin_vigencia, 0 as id_empresa_entidad, 'E' as Id_Tipo_Entidad_entidad, '' as horario_atencion, '' as referente_1, '' as referente_2, '' as calle_entidad, 0 as nro_puerta_entidad, 0 as piso_entidad, '' as departamento_entidad, 0 as id_localidad_entidad, 0 as caracteristica_te_entidad, 0 as nro_telefono_entidad, '' as mail_entidad, 0 as id_categoria_entidad FROM AUX_D_ENTIDADES WHERE id_entidad not in (SELECT id_entidad FROM d_entidades)
/* Actualiza la id_categoria_entidad con un 1 si esta es un car en la as400 */
update D_ENTIDADES set id_categoria_entidad = 1 from D_ENTIDADES d inner join [HOST03].[HOST03].[SMPLIBD].[M09FASE] as400 on d.id_entidad = as400.w09nas where as400.w09cen = 'S'
