/* Completo Temporal para el informe de planes nuevos */
INSERT INTO INTRANET.INTRANETX.dbo.Planes_Nuevos (Id_Producto, Id_Plan_Producto, De_Planes) SELECT Id_Producto, Id_Plan_Producto, De_Planes FROM AUX_D_PLANES a11 WHERE not EXISTS (SELECT * FROM d_planes a12 WHERE id_producto = a11.Id_Producto and id_plan_producto = a11.Id_Plan_Producto)
/* Actualizo la información existente */
UPDATE D_PLANES SET de_planes = a12.de_planes, de_plan_reducida = a12.de_plan_reducida, orden_plan = a12.orden_plan, id_plan_activo = a12.id_plan_activo, visualizacion_web = a12.visualizacion_web FROM D_PLANES D INNER JOIN AUX_D_PLANES a12 ON D.id_producto = a12.Id_Producto and D.id_plan_producto = a12.id_plan_producto
/* Inserta nueva información en la D_PLANES desde la AUXILIAR */
INSERT INTO D_PLANES (id_producto, id_plan_producto, de_planes, de_plan_reducida, orden_plan, id_plan_activo, visualizacion_web, Id_Plan_Agrupado, Id_Rubro_Salud, Id_Tipo_Planes, Id_Subrubro_Ley, Id_Plan_Agrupado_Convenio, Id_SubRubro_Ley_Agrupado) SELECT id_producto, id_plan_producto, de_planes, de_plan_reducida, orden_plan, id_plan_activo, visualizacion_web, Id_Plan_Agrupado, ' ' as Id_Rubro_Salud, null as Id_Tipo_Planes, 0 as Id_Subrubro_Ley, 0 as Id_Plan_Agrupado_Convenio, 0 as Id_SubRubro_Ley_agrupado FROM AUX_D_PLANES a11 WHERE not EXISTS (SELECT * FROM D_PLANES a12 WHERE id_producto = a11.id_producto and id_plan_producto = a11.id_plan_producto)
/* Actualiza id_plan_Activo = NO si no tiene información */
UPDATE D_PLANES SET id_plan_activo = 'NO' WHERE id_plan_activo is null

