/* Borro un promotor erroneo que viene apareciendo en el TXT y que ya esta cargado correctamente en D_PROMOTOR */
DELETE FROM consultas_dw.dbo.AUX_D_PROMOTOR WHERE id_promotor = '00284R'
/* Actualizo la información existente */ UPDATE d_promotor SET de_promotor = a12.de_promotor, id_zona_comarcal = a12.id_zona_comarcal, id_posicion_relacionada = a12.id_posicion_relacionada, fecha_fin_vigencia= case when isdate(a12.fecha_fin_vigencia)=1 then convert(datetime, a12.fecha_fin_vigencia) else null end FROM consultas_dw.dbo.AUX_D_PROMOTOR a12 Inner join D_PROMOTOR D ON D.id_promotor = a12.id_promotor
/* Creo la información faltante */ INSERT INTO d_promotor (id_promotor, de_promotor, id_zona_comarcal, id_posicion_relacionada, fecha_fin_vigencia, id_zona_comercial_promotor, id_supervisor_ultimo_del_promotor ) SELECT id_promotor, de_promotor, id_zona_comarcal, id_posicion_relacionada, case when isdate(fecha_fin_vigencia)=1 then convert(datetime, fecha_fin_vigencia) else null end, 0 as id_zona_comercial_promotor, 99999 as id_supervisor_ultimo_del_promotor FROM consultas_dw.dbo.AUX_D_PROMOTOR WHERE id_promotor not in (SELECT id_promotor FROM d_promotor)
/* Actualiza la info de id_supervisor_ultimo_del_promotor */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = id_asesor_superior_cascada_comercial from D_PROMOTOR PROM inner join H_CASCADA_COMERCIAL CAS ON (PROM.id_promotor = CAS.id_asesor_cascada_comercial) WHERE id_tipo_puesto_superior_cascada_comercial in (3, 2, 23) and id_periodo = (select MAX(id_periodo) from H_CASCADA_COMERCIAL)
/* si quedo un asesor lider como ultimo supervisor se busca su superior */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = id_asesor_superior_cascada_comercial from D_PROMOTOR PROM inner join H_CASCADA_COMERCIAL CAS ON (PROM.id_supervisor_ultimo_del_promotor = CAS.id_asesor_cascada_comercial) WHERE id_tipo_puesto_cascada_comercial in (2) and id_periodo = (select MAX(id_periodo) from H_CASCADA_COMERCIAL)
/* Si el promotor por algun motivo no tienen un supervisor, se le asigna el valor por defecto */
update D_PROMOTOR set id_supervisor_ultimo_del_promotor = 99999 from D_PROMOTOR Where id_supervisor_ultimo_del_promotor is null
