/* Actualizo la información existente */ UPDATE d_subgrupo_plan SET d_subgrupo_plan.de_subgrupo_plan = a12.de_subgrupo_plan, d_subgrupo_plan.de_subgrupo_plan_reducida = a12.de_subgrupo_plan_reducida, d_subgrupo_plan.id_plan_activo = a12.id_plan_activo, d_subgrupo_plan.visualizacion_web = a12.visualizacion_web FROM aux_d_subgrupo_plan a12 WHERE d_subgrupo_plan.id_producto = a12.id_producto and d_subgrupo_plan.id_plan_producto = a12.id_plan_producto and d_subgrupo_plan.subgrupo_plan = a12.subgrupo_plan
/* Creo la información faltante */ INSERT INTO d_subgrupo_plan SELECT distinct id_producto, id_plan_producto, subgrupo_plan, de_subgrupo_plan, de_subgrupo_plan_reducida, id_plan_activo, visualizacion_web FROM aux_d_subgrupo_plan a11 WHERE not EXISTS (SELECT * FROM d_subgrupo_plan a12 WHERE id_producto = a11.id_producto and id_plan_producto = a11.id_plan_producto and subgrupo_plan = a11.subgrupo_plan)
/* Actualiza id_plan_Activo = NO si no tiene información */
UPDATE d_subgrupo_plan SET d_subgrupo_plan.id_plan_activo = 'NO' WHERE id_plan_activo is null
