/* Creo la información faltante */
INSERT INTO d_empresas (id_empresa, de_empresa, cuit_empresa, Id_Tipo_Entidad, Id_Corporativo, id_ramo_empresa,id_zona_comercial_empresa, observacion_empresa, calle_empresa, Nro_puerta_empresa,Piso_empresa,Dpto_empresa, Barrio_empresa,id_localidad_empresa, cpa_localidad_empresa, Caracteristica_te_empresa, Nro_telefono_empresa, mail_empresa) SELECT id_empresa, de_empresa, cuit_empresa, 'E' as Id_Tipo_Entidad, 0 as Id_Corporativo, id_ramo_empresa, 0 as id_zona_comercial_empresa, ' ' as observacion_empresa, '' as calle_empresa, 0 as Nro_puerta_empresa, 0 as Piso_empresa, 0 as Dpto_empresa, '' as Barrio_empresa, 9999999 as id_localidad_empresa, 9999999 as cpa_localidad_empresa, ' ' as Caracteristica_te_empresa, ' ' as Nro_telefono_empresa, ' ' as mail_empresa FROM aux_d_empresas WHERE id_empresa not in (SELECT id_empresa FROM d_empresas)
/* Actualizo la información existente */ UPDATE d_empresas SET d_empresas.de_empresa = a12.de_empresa, d_empresas.cuit_empresa = a12.cuit_empresa, d_empresas.id_ramo_empresa = a12.id_ramo_empresa FROM d_empresas E INNER JOIN aux_d_empresas a12 ON E.id_empresa = a12.id_empresa AND E.Id_Tipo_Entidad = 'E'
