/* Actualiza la información existente. Si no estan dadas de baja actualizo tmb el copago */ UPDATE D_GRILLAS SET d_grillas.de_grilla = a12.de_grilla, d_grillas.char_fecha_vig_desde = a12.char_fecha_vig_desde, d_grillas.char_fecha_vig_hasta = a12.char_fecha_vig_hasta, d_grillas.id_con_copago = a12.id_con_copago FROM aux_d_grillas a12 WHERE d_grillas.id_producto = a12.id_producto and d_grillas.id_plan_producto = a12.id_plan_producto and d_grillas.grilla = a12.grilla and a12.id_plan_producto not in ('OS', 'SX','PMO') --se excluyen los planes OS y SX xq no tienen liquidacion, por lo tanto no tienen LPO and a12.char_fecha_vig_hasta = 0
/* Actualiza info para las que se dieron de baja. aqui no actualizo el copago para no perder la info que tengo en mi tabla*/ UPDATE D_GRILLAS SET d_grillas.de_grilla = a12.de_grilla, d_grillas.char_fecha_vig_desde = a12.char_fecha_vig_desde, d_grillas.char_fecha_vig_hasta = a12.char_fecha_vig_hasta FROM aux_d_grillas a12 WHERE d_grillas.id_producto=a12.id_producto and d_grillas.id_plan_producto = a12.id_plan_producto and d_grillas.grilla = a12.grilla and a12.id_plan_producto not in ('OS', 'SX','PMO') --se excluyen los planes OS y SX xq no tienen liquidacion, por lo tanto no tienen LPO and a12.char_fecha_vig_hasta <> 0 --si una grilla se dio de baja trae fecha, si no trae 0
/* Creo la información faltante */ INSERT INTO D_GRILLAS (id_producto, id_plan_producto, grilla, de_grilla, char_fecha_vig_desde, char_fecha_vig_hasta, id_con_copago, fecha_vig_desde, fecha_vig_hasta) SELECT a11.id_producto, a11.id_plan_producto, a11.grilla, a11.de_grilla, a11.char_fecha_vig_desde, a11.char_fecha_vig_hasta, id_con_copago, fecha_vig_desde, fecha_vig_hasta FROM aux_d_grillas a11 WHERE not exists (SELECT * FROM d_grillas a12 WHERE a11.id_producto = a12.id_producto and a11.id_plan_producto = a12.id_plan_producto and a11.grilla = a12.grilla)
/* Corrige de fechas. */ UPDATE D_GRILLAS set char_fecha_vig_desde = '19000101' where isdate(substring(char_fecha_vig_desde,1,4) + '-' + substring(char_fecha_vig_desde,5,2) + '-' + substring(char_fecha_vig_desde,7,2) + ' ') = 0
UPDATE D_GRILLAS set char_fecha_vig_hasta = '19000101' where isdate(substring(char_fecha_vig_hasta,1,4) + '-' + substring(char_fecha_vig_hasta,5,2) + '-' + substring(char_fecha_vig_hasta,7,2) + ' ') = 0
UPDATE D_GRILLAS SET fecha_vig_desde = char_fecha_vig_desde, fecha_vig_hasta = char_fecha_vig_hasta

