Declare @Periodo Int Set @Periodo = (Select id_periodo from aux_periodo_mes_borra)
/* Actualiza Fechas de Ingreso en base a la minima secuencia del historial de la cuenta desde H_BAJAS_COMP_GRUPO. */
-- fecha_inicio_serv, fecha_ingreso_Sancor_Salud, periodo_inicio_serv, periodo_ingreso_salud. SELECT a11.id_periodo, a11.afiliado, a11.subnumero, a11.fecha_inicio_serv, a11.fecha_ingreso_Sancor_Salud, a11.periodo_inicio_serv, a11.periodo_ingreso_salud INTO #TH_GRUPO_BAJA FROM H_BAJAS_COMP_GRUPO a11 INNER JOIN (Select id_periodo, afiliado, subnumero, min(secuencia) as secuencia from h_bajas_comp_grupo group by id_periodo, afiliado, subnumero) MinSecuencia ON a11.id_periodo = MinSecuencia.id_periodo and a11.afiliado = MinSecuencia.afiliado and a11.subnumero = MinSecuencia.subnumero and a11.secuencia = MinSecuencia.secuencia
Update AUX_H_COMER_GRUPO_BAJA set fecha_inicio_serv = isnull(a11.fecha_inicio_serv, '1900-01-01 '), fecha_ingreso_Sancor_Salud = isnull(a11.fecha_ingreso_Sancor_Salud, '1900-01-01 '), periodo_inicio_serv = isnull(a11.periodo_inicio_serv, 190001), periodo_ingreso_salud = isnull(a11.periodo_ingreso_salud,190001) from AUX_H_COMER_GRUPO_BAJA C left outer join #TH_GRUPO_BAJA a11 on C.id_periodo = a11.id_periodo and C.afiliado = a11.afiliado and C.subnumero = a11.subnumero
DROP TABLE #TH_GRUPO_BAJA
/* Actualiza aporte_basico, aporte_voluntario y fecha_periodo_actual. */
UPDATE AUX_H_COMER_GRUPO_BAJA SET fecha_periodo_actual = cast(left(AUX_H_COMER_GRUPO_BAJA.id_periodo, 4)+'-'+ right(AUX_H_COMER_GRUPO_BAJA.id_periodo, 2) +'-'+'01 ' as datetime), aporte_basico = aporte_basico / 100, aporte_voluntario = aporte_voluntario / 100
/* Actualiza Categoria desde H_COMP_GRUPO_ACTIVO. */
-- A partir del mes 11/2012 la categoria viene de sist. y la replicamos. UPDATE AUX_H_COMER_GRUPO_BAJA SET Id_Categoria = A.Id_Categoria FROM AUX_H_COMER_GRUPO_BAJA H1 INNER JOIN H_COMP_GRUPO_ACTIVO A ON H1.id_periodo = A.id_periodo AND H1.id_Persona = A.id_persona WHERE H1.id_periodo = @Periodo
UPDATE AUX_H_COMER_GRUPO_BAJA SET Id_Categoria = 9 WHERE Id_Categoria is null or Id_Categoria not in (SELECT id_categoria FROM d_categoria_afiliado) or id_categoria = ' '

