-- Utilizando el Periodo VIGENTE desde H_COMER_STOCK_AFILIA. UPDATE AUX_H_COMER_TRANSFERENCIAS_CAB set afiliado = STOCKA.afiliado, subnumero = STOCKA.subnumero, secuencia = STOCKA.secuencia FROM AUX_H_COMER_TRANSFERENCIAS_CAB TCAB JOIN H_COMER_STOCK_AFILIA STOCKA on TCAB.id_persona = STOCKA.id_persona AND STOCKA.id_periodo = TCAB.id_periodo AND TCAB.id_producto = STOCKA.id_producto AND TCAB.id_plan_producto = STOCKA.id_plan_producto WHERE (TCAB.afiliado is null or TCAB.subnumero is null or TCAB.secuencia is null) and comer_cant_transferencia = 1
/* Actualiza campo Grilla desde H_COMER_STOCK_AFILIA. */ Declare @Periodo_Stock Int Set @Periodo_Stock = (select MAX(id_periodo) from H_COMER_STOCK_AFILIA) Declare @Periodo_Anterior Int Set @Periodo_Anterior = (select MAX(id_periodo) from H_COMER_STOCK_AFILIA where id_periodo < @Periodo_Stock)
-- Las transferencias Positivas las actualiza desde el periodo anterior. UPDATE AUX_H_COMER_TRANSFERENCIAS_CAB SET grilla = S.grilla FROM AUX_H_COMER_TRANSFERENCIAS_CAB a JOIN H_COMER_STOCK_AFILIA s on a.id_periodo = s.id_periodo and a.id_persona = s.id_persona and a.id_producto = s.id_producto and a.id_subproducto = s.id_subproducto and a.id_plan_producto = s.id_plan_producto where s.id_periodo = @Periodo_Anterior and a.comer_cant_transferencia = 1
-- Las transferencias Negativas las actualiza desde el periodo anterior.
UPDATE AUX_H_COMER_TRANSFERENCIAS_CAB SET grilla = S.grilla FROM AUX_H_COMER_TRANSFERENCIAS_CAB a JOIN H_COMER_STOCK_AFILIA s on a.id_persona = s.id_persona and a.id_producto = s.id_producto and a.id_subproducto = s.id_subproducto and a.id_plan_producto = s.id_plan_producto where s.id_periodo = @Periodo_Anterior and a.comer_cant_transferencia = -1

