select * into #OS_faltantes from h_comer_stock_afilia h 
where not exists ( select * from [TRANS_D_SUBPRODUCTOS_OS] t where t.id_producto = h.id_producto and t.id_subproducto = h.id_subproducto)
and h.id_periodo = @periodo


		-- Informa
		IF (select COUNT(*) 
		from #OS_faltantes ) > 0

