--Inserta el nuevo periodo INSERT INTO TMP_CANT_PERIODO_H_ORD_AMB_DET (id_periodo, cantidad, costo, costounitario) SELECT id_periodo, COUNT(id_periodo) as cantidad, 0.0 as costo, 0.0 as costounitario FROM AUX_H_ORD_AMB_DET WHERE id_acreedor = 122875 and id_reintegro = 'C' and id_periodo = @periodo and @periodo not in (select id_periodo from TMP_CANT_PERIODO_H_ORD_AMB_DET) --Asegura no duplicar información de un mismo periodo. GROUP BY id_periodo
--Setea el costo y el costo unitario UPDATE TMP_CANT_PERIODO_H_ORD_AMB_DET SET costo = t.Importe_Facturado, costounitario = t.Importe_Facturado / cantidad FROM TMP_CANT_PERIODO_H_ORD_AMB_DET H JOIN ##temp1 T on left(T.Fecha_Recepcion,6) = h.id_periodo
/* Actualiza la AUX_H_ORD_AMB_DET */ UPDATE AUX_H_ORD_AMB_DET SET imp_prest_amb_acep = costounitario, imp_prest_amb_recon = costounitario, imp_pret_amb_pag = costounitario, imp_prest_amb_fact = costounitario FROM AUX_H_ORD_AMB_DET H JOIN TMP_CANT_PERIODO_H_ORD_AMB_DET T on H.id_periodo = T.id_periodo WHERE id_acreedor = 122875 and id_reintegro = 'C' and h.id_periodo = (select max(id_periodo) from TMP_CANT_PERIODO_H_ORD_AMB_DET)
