/* Actualiza la información existente. */ UPDATE D_CAP_SUBCAP_SECC SET de_cap_subcap_secc = a12.de_cap_subcap_secc FROM D_CAP_SUBCAP_SECC D INNER JOIN AUX_D_CAP_SUBCAP_SECC a12 ON D.id_nomenclador = a12.id_nomenclador and D.id_capitulo = a12.id_capitulo and D.id_subcapitulo = a12.id_subcapitulo and D.id_seccion = a12.id_seccion
/* Inserta la información faltante */ INSERT INTO D_CAP_SUBCAP_SECC (id_nomenclador, id_capitulo, id_subcapitulo, id_seccion, de_cap_subcap_secc) SELECT id_nomenclador, id_capitulo, id_subcapitulo, id_seccion, de_cap_subcap_secc FROM AUX_D_CAP_SUBCAP_SECC a11 WHERE not EXISTS (SELECT * FROM d_cap_subcap_secc a12 WHERE id_nomenclador = a11.id_nomenclador and id_capitulo = a11.id_capitulo and id_subcapitulo = a11.id_subcapitulo and id_seccion = a11.id_seccion) Group by id_nomenclador, id_capitulo, id_subcapitulo, id_seccion, de_cap_subcap_secc
/* Inserta casos existentes en D_PRESTACION y no en D_CAP_SUBCAP_SECC */ INSERT INTO D_CAP_SUBCAP_SECC (id_nomenclador, id_capitulo, id_subcapitulo, id_seccion, de_cap_subcap_secc) SELECT C.id_nomenclador, C.id_capitulo, C.id_subcapitulo, 0 as id_seccion, 'Sin Dato' as de_cap_subcap_secc FROM D_PRESTACION C WHERE not exists (select S.id_nomenclador, S.id_capitulo, S.id_subcapitulo from D_CAP_SUBCAP_SECC S where S.id_nomenclador = C.id_nomenclador and S.id_capitulo = C.id_capitulo and S.id_subcapitulo = C.id_subcapitulo ) GROUP BY C.id_nomenclador, C.id_capitulo, C.id_subcapitulo
