/* Actualiza la información existente en D_POSICION. */ UPDATE D_POSICION SET de_posicion = U01DES FROM AS400.HOST03.SMPDATD.U01FTPOS A INNER JOIN D_POSICION P ON (A.W01CNV=P.id_posicion) where U01DES COLLATE SQL_Latin1_General_CP850_CI_AI<>de_posicion
/* Inserta la información faltante en D_POSICION. */ INSERT INTO D_POSICION (id_posicion, de_posicion) SELECT W01CNV as id_posicion, U01DES as de_posicion from AS400.HOST03.SMPDATD.U01FTPOS WHERE W01CNV not in (SELECT id_posicion FROM d_posicion)
/* Inserta la información faltante en D_POSICION desde las Localidades del Afiliado. */ INSERT INTO D_POSICION (id_posicion, de_posicion) SELECT DISTINCT Id_Posicion, 'F/D' as de_posicion FROM D_LOCALIDADES_AFILIADO WHERE id_posicion NOT IN (select id_posicion from d_posicion)

/* Actualiza la información existente en D_POSICION_ESTAB_ASIST. */ UPDATE D_POSICION_ESTAB_ASIST SET de_posicion_estab_asist = U01DES FROM AS400.HOST03.SMPDATD.U01FTPOS A INNER JOIN D_POSICION_ESTAB_ASIST P ON (A.W01CNV=P.id_posicion_estab_asist) where U01DES COLLATE SQL_Latin1_General_CP850_CI_AI<>de_posicion_estab_asist
/* Actualiza la información existente en D_POSICION_ESTAB_ASIST. */ UPDATE D_POSICION_ESTAB_ASIST SET Id_Auditoria_Internacion = a12.Id_Auditoria_Internacion FROM D_LOCALIDADES a12 WHERE id_posicion_estab_asist = a12.id_posicion
/* Inserta la información faltante en D_POSICION_ESTAB_ASIST. */ INSERT INTO D_POSICION_ESTAB_ASIST (id_posicion_estab_asist, de_posicion_estab_asist, Id_Auditoria_Internacion) SELECT W01CNV as id_posicion_estab_asist, U01DES as de_posicion_estab_asist, 'X' as Id_Auditoria_Internacion FROM AS400.HOST03.SMPDATD.U01FTPOS WHERE W01CNV not in (SELECT id_posicion_estab_asist FROM d_posicion_estab_asist)
