/* Actualiza Provisión de la Prestación. */ UPDATE D_PRESTACION SET id_provision_prest = S.PSE_COD FROM D_PRESTACION A JOIN HOST03.HOST03.DESARROLLO.CUP001 S on A.de_codigo_prestacion = S.NMP_CPR COLLATE SQL_Latin1_General_CP850_CI_AI and A.id_nomenclador = S.TNO_COD COLLATE SQL_Latin1_General_CP850_CI_AI
