-- Siempre pone todo en cero ya que pueden cambiar las condiciones de Recupero por cada tabla. UPDATE D_PRESTACION set id_marca_recupero_sur = 0
-- Actualiza Marca Recupero. UPDATE D_PRESTACION SET id_marca_recupero_sur = 1 FROM D_PRESTACION A JOIN HOST03.HOST03.DESARROLLO.CUP001L S on A.de_codigo_prestacion = S.NMP_CPR COLLATE SQL_Latin1_General_CP850_CI_AI and A.id_nomenclador = S.TNO_COD COLLATE SQL_Latin1_General_CP850_CI_AI WHERE S.NMPCPL = 90
/* Actualiza datos existentes. */
UPDATE D_PRESTACION SET de_prestacion = d.de_prestacion, id_capitulo = d.id_capitulo, id_subcapitulo = d.id_subcapitulo, id_seccion = d.id_seccion, id_grupo_prestacion = d.id_grupo_prestacion, id_subgrupo_prestacion = d.id_subgrupo_prestacion, id_accion_farmacologica = d.id_accion_farmacologica, id_servicio_prest = d.id_servicio_prest, id_catalogada = d.id_catalogada, id_cod_resolucion = d.id_cod_resolucion, id_nivel_capitacion = d.id_nivel_capitacion, id_complejidad = d.id_complejidad, id_nomenclador = d.id_nomenclador, de_codigo_prestacion = d.de_codigo_prestacion FROM D_PRESTACION a JOIN AUX_PRESTACION d on a.id_prestacion = d.id_prestacion
/* Corrige Nomencladores y Cod de Prestaciones para las prestaciones ingresadas desde los Datas. */
UPDATE D_PRESTACION set id_nomenclador = '99', de_codigo_prestacion = '99999999' WHERE SUBSTRING(id_prestacion,1,2) not in (select id_nomenclador from D_NOMENCLADOR) and id_nomenclador <> '99'

