/* Actualizo la tabla de ACREEDOR */ INSERT INTO D_ACREEDOR (id_acreedor, De_Acreedor, De_ACreedor_reduc, id_categoria_acreedor, id_provincia, Id_localidad, subcodpos, id_posicion_acreedor, id_Tipo_prestador, simulador_convenio, evolucion_pareto, orden_pareto) SELECT id_acreedor, MAX('F/D') as De_Acreedor, MAX('F/D') as De_ACreedor_reduc, MAX(99999) as id_categoria_acreedor, MAX(9) as id_provincia, MAX(9999) as Id_localidad, MAX(99) as subcodpos, MAX(999) as id_posicion_acreedor, IsNull(id_Tipo_prestador,'P'), 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto FROM AUX_CREDITOS_DEBITOS H WHERE NOT EXISTS (Select * from d_acreedor D WHERE H.id_acreedor = D.id_acreedor AND H.id_tipo_prestador = D.id_tipo_prestador) GROUP BY id_acreedor, id_Tipo_prestador

