/* Actualizo la tabla de MARCA VALORIZACION*/ INSERT INTO D_MARCA_VALORIZACION (id_marca_valorizacion, de_marca_valorizacion) SELECT DISTINCT id_marca_valorizacion, 'F/D' as de_marca_valorizacion FROM AUX_H_ORD_INT_DET WHERE id_marca_valorizacion not in (SELECT id_marca_valorizacion FROM d_marca_valorizacion)
/* Actualizo la tabla de TIPO REFACTURACION*/ INSERT INTO D_TIPO_REFACTURACION (id_tipo_refacturacion, de_tipo_refacturacion) SELECT DISTINCT id_tipo_refacturacion, ' ' as de_tipo_refacturacion FROM AUX_H_ORD_INT_DET WHERE id_tipo_refacturacion not in (SELECT id_tipo_refacturacion FROM d_tipo_refacturacion)
/* Actualizo la tabla de TIPO PAPEL*/ INSERT INTO D_TIPO_PAPEL (id_tipo_papel, de_tipo_papel) SELECT DISTINCT id_tipo_papel, 'F/D' as de_tipo_papel FROM AUX_H_ORD_INT_DET WHERE id_tipo_papel not in (SELECT id_tipo_papel FROM d_tipo_papel)
/* Actualizo la tabla de AFILIADOS */ INSERT INTO D_COMP_GRUPO_AFILIACION (afiliado, Subnumero, secuencia, id_persona) SELECT DISTINCT afiliado, subnumero, secuencia, 999999 as id_persona FROM AUX_H_ORD_INT_DET a11 WHERE not exists (SELECT * FROM d_comp_grupo_afiliacion a12 WHERE a11.afiliado = a12.afiliado and a11.subnumero = a12.subnumero and a11.secuencia = a12.secuencia)
/* Actualizo la tabla de GRUPOS DE AFILIADOS */ INSERT INTO D_GRUPO_AFILIACION (afiliado, Subnumero, id_ocupacion) SELECT DISTINCT afiliado, subnumero, 999 as id_ocupacion FROM AUX_H_ORD_INT_DET a11 WHERE not exists (SELECT * FROM d_grupo_afiliacion a12 WHERE a11.afiliado = a12.afiliado and a11.subnumero = a12.subnumero)
/* Actualiza la tabla de GRILLAS */ INSERT INTO D_GRILLAS (id_producto, id_plan_producto, grilla, de_grilla, char_fecha_vig_desde, char_fecha_vig_hasta, id_con_copago, fecha_vig_desde, fecha_vig_hasta) SELECT H.id_producto, H.id_plan_producto, H.grilla, max('No Definida') as De_Grilla, max('19000101') as Char_Fecha_Vig_Desde, max('19000101') as Char_Fecha_Vig_Hasta, max('X') as Id_Con_Copago, max('1900-01-01 ') as Fecha_Vig_Desde, max('1900-01-01 ') as Fecha_Vig_Hasta FROM AUX_H_ORD_INT_DET H WHERE not exists (Select * from d_grillas G where H.id_producto = G.id_producto and H.id_plan_producto = G.id_plan_producto and H.grilla = G.grilla) GROUP BY H.id_producto, H.id_plan_producto, H.grilla

/* Actualiza la tabla de PLANES */
-- Actualiza tabla disparadora Consultas_DW.dbo.NOTIFICACION_Planes_Dimensiones. INSERT INTO Consultas_DW.dbo.NOTIFICACION_Planes_Dimensiones (id_producto, id_plan_producto, informado, observaciones) SELECT DISTINCT id_producto, id_plan_producto, 0 as informado, 'AUX_H_ORD_INT_DET' as observaciones FROM AUX_H_ORD_INT_DET D where not exists (select P.id_producto, P.id_plan_producto from D_PLANES P where D.id_producto = P.id_producto and D.id_plan_producto = P.id_plan_producto)
/* Actualiza la tabla de PRODUCTOS. */
-- Actualiza tabla disparadora Consultas_DW.dbo.NOTIFICACION_Planes_Dimensiones. INSERT INTO Consultas_DW.dbo.NOTIFICACION_Producto_Dimensiones (id_producto, de_producto, informado, observaciones) SELECT DISTINCT id_producto, 'F/D' as de_producto, 0 as informado, 'AUX_H_ORD_INT_DET' observaciones FROM AUX_H_ORD_INT_DET D where id_producto not in (select distinct P.id_producto from D_PRODUCTOS P)
/* Actualiza la tabla de ACREEDOR. */
-- Actualiza tabla disparadora Consultas_DW.dbo.NOTIFICACION_ACREEDOR_Dimensiones. INSERT INTO Consultas_DW.dbo.NOTIFICACION_ACREEDOR_Dimensiones (id_acreedor, id_Tipo_prestador, informado, observaciones) SELECT DISTINCT id_acreedor, id_Tipo_prestador, 0 as informado, 'AUX_H_ORD_INT_DET' observaciones FROM AUX_H_ORD_INT_DET D where id_acreedor not in (select distinct P.id_acreedor from D_ACREEDOR P)
/* Actualiza la tabla de GRILLAS. */
-- Actualiza tabla disparadora Consultas_DW.dbo.NOTIFICACION_Planes_Dimensiones. INSERT INTO Consultas_DW.dbo.NOTIFICACION_Grillas_Dimensiones (id_producto, id_plan_producto, grilla, informado, observaciones) SELECT DISTINCT id_producto, id_plan_producto, grilla, 0 as informado, 'AUX_H_ORD_INT_DET' as observaciones FROM AUX_H_ORD_INT_DET D where not exists (select P.id_producto, P.id_plan_producto, P.grilla from D_GRILLAS P where D.id_producto = P.id_producto and D.id_plan_producto = P.id_plan_producto and D.grilla = P.grilla)
