-- Inserta casos nuevos. INSERT INTO D_PRESTACION_HOMOLOGADA_ACREEDOR (id_prestacion, id_nomenclador, de_codigo_prestacion, id_acreedor, id_tipo_prestador, id_prestacion_acreedor, id_nomenclador_acreedor, de_codigo_prestacion_acreedor, id_codigo_agrupador_acreedor, de_prestacion_acreedor, fecha_baja) select SS.S48NOM+SS.W_M23PRE as id_prestacion, SS.S48NOM as id_nomenclador, SS.W_M23PRE as de_codigo_prestacion, AC.T48NPR as id_acreedor, 'P' as id_tipo_prestador, AC.T48NOM+AC.W_M23PRE as id_prestacion_acreedor, AC.T48NOM as id_nomenclador_acreedor, AC.W_M23PRE as de_codigo_prestacion_acreedor, AC.T48GPO as id_codigo_agrupador_acreedor, '' as de_prestacion_acreedor, AC.T48FBA as fecha_baja FROM HOST03.HOST03.SMPORDD.T148FPRP AC JOIN HOST03.HOST03.SMPORDD.T148FPRS SS on SS.S48GPO = AC.T48GPO and SS.S48NPR = AC.T48NPR WHERE not exists (select id_prestacion, id_nomenclador, de_codigo_prestacion, id_acreedor, id_tipo_prestador, id_prestacion_acreedor, id_nomenclador_acreedor, de_codigo_prestacion_acreedor, id_codigo_agrupador_acreedor from D_PRESTACION_HOMOLOGADA_ACREEDOR H where SS.S48NOM COLLATE SQL_Latin1_General_CP850_CI_AI + SS.W_M23PRE COLLATE SQL_Latin1_General_CP850_CI_AI = H.id_prestacion and AC.T48NPR = H.id_acreedor and AC.T48NOM COLLATE SQL_Latin1_General_CP850_CI_AI + AC.W_M23PRE COLLATE SQL_Latin1_General_CP850_CI_AI = H.id_prestacion_acreedor and AC.T48GPO = H.id_codigo_agrupador_acreedor)
