-- Ambulatorio. INSERT INTO D_PRESTACION_HOMOLOGADA_ACREEDOR (id_prestacion, id_nomenclador, de_codigo_prestacion, id_acreedor, id_tipo_prestador, id_prestacion_acreedor, id_nomenclador_acreedor, de_codigo_prestacion_acreedor, id_codigo_agrupador_acreedor, de_prestacion_acreedor, fecha_baja) SELECT id_prestacion, SUBSTRING(id_prestacion,1,2) as id_nomenclador, SUBSTRING(id_prestacion,3,10) as de_codigo_prestacion, id_acreedor, 'P' as id_tipo_prestador, id_prestacion_acreedor, SUBSTRING(id_prestacion_acreedor,1,2) as id_nomenclador_acreedor, SUBSTRING(id_prestacion_acreedor,3,10) as de_codigo_prestacion_acreedor, 0 as id_codigo_agrupador_acreedor, '' as de_prestacion_acreedor, 9999 as fecha_baja FROM H_ORD_AMB_DET C WHERE not exists (select id_acreedor, id_prestacion, id_prestacion_acreedor from D_PRESTACION_HOMOLOGADA_ACREEDOR P where C.id_acreedor = P.id_acreedor and C.id_prestacion = P.id_prestacion and C.id_prestacion_acreedor = P.id_prestacion_acreedor) and id_prestacion_acreedor not in ('9999999999') and id_periodo in (SELECT MAX(id_periodo) from D_PERIODO where vigente_consumo = 1) group by id_prestacion, SUBSTRING(id_prestacion,1,2), SUBSTRING(id_prestacion,3,10), id_acreedor, id_prestacion_acreedor, SUBSTRING(id_prestacion_acreedor,1,2), SUBSTRING(id_prestacion_acreedor,3,10)
-- Internacion. INSERT INTO D_PRESTACION_HOMOLOGADA_ACREEDOR (id_prestacion, id_nomenclador, de_codigo_prestacion, id_acreedor, id_tipo_prestador, id_prestacion_acreedor, id_nomenclador_acreedor, de_codigo_prestacion_acreedor, id_codigo_agrupador_acreedor, de_prestacion_acreedor, fecha_baja) SELECT id_prestacion, SUBSTRING(id_prestacion,1,2) as id_nomenclador, SUBSTRING(id_prestacion,3,10) as de_codigo_prestacion, id_acreedor, 'P' as id_tipo_prestador, id_prestacion_acreedor, SUBSTRING(id_prestacion_acreedor,1,2) as id_nomenclador_acreedor, SUBSTRING(id_prestacion_acreedor,3,10) as de_codigo_prestacion_acreedor, 0 as id_codigo_agrupador_acreedor, '' as de_prestacion_acreedor, 9999 as fecha_baja FROM H_ORD_INT_DET C WHERE not exists (select id_acreedor, id_prestacion, id_prestacion_acreedor from D_PRESTACION_HOMOLOGADA_ACREEDOR P where C.id_acreedor = P.id_acreedor and C.id_prestacion = P.id_prestacion and C.id_prestacion_acreedor = P.id_prestacion_acreedor) and id_prestacion_acreedor not in ('9999999999') and id_periodo in (SELECT MAX(id_periodo) from D_PERIODO where vigente_consumo = 1) group by id_prestacion, SUBSTRING(id_prestacion,1,2), SUBSTRING(id_prestacion,3,10), id_acreedor, id_prestacion_acreedor, SUBSTRING(id_prestacion_acreedor,1,2), SUBSTRING(id_prestacion_acreedor,3,10)
/* Actualiza la fecha de baja de la Prestacion Homologada. */
UPDATE D_PRESTACION_HOMOLOGADA_ACREEDOR SET fecha_baja = T48FBA FROM HOST03.HOST03.SMPORDD.T148FPRP AC JOIN D_PRESTACION_HOMOLOGADA_ACREEDOR SS on SS.id_codigo_agrupador_acreedor = AC.T48GPO and SS.id_acreedor = AC.T48NPR and SS.id_nomenclador_acreedor = AC.T48NOM COLLATE Modern_Spanish_CI_AS and SS.de_codigo_prestacion_acreedor = AC.W_M23PRE COLLATE Modern_Spanish_CI_AS WHERE T48FBA <> 0
/* Actualiza con fecha de baja 9999 las Prestaciones Homologadas que se eliminan desde Contrataciones en las tablas, por ende no se encuentran en las tablas del AS. */
UPDATE D_PRESTACION_HOMOLOGADA_ACREEDOR SET fecha_baja = 9999 from D_PRESTACION_HOMOLOGADA_ACREEDOR A where not exists ( select SS.S48NOM+SS.W_M23PRE as id_prestacion, AC.T48NPR as id_acreedor, AC.T48NOM+AC.W_M23PRE as id_prestacion_acreedor, AC.T48GPO as id_codigo_agrupador_acreedor FROM HOST03.HOST03.SMPORDD.T148FPRP AC JOIN HOST03.HOST03.SMPORDD.T148FPRS SS on SS.S48GPO = AC.T48GPO and SS.S48NPR = AC.T48NPR where SS.S48NOM+SS.W_M23PRE COLLATE SQL_Latin1_General_CP850_CI_AI = A.id_prestacion and AC.T48NPR = A.id_acreedor and AC.T48NOM+AC.W_M23PRE COLLATE SQL_Latin1_General_CP850_CI_AI = A.id_prestacion_acreedor and AC.T48GPO = A.id_codigo_agrupador_acreedor)
/* Actualiza campo de_prestacion_acreedor en D_PRESTACION_HOMOLOGADA_ACREEDOR. */ UPDATE D_PRESTACION_HOMOLOGADA_ACREEDOR SET de_prestacion_acreedor = CAST(SA.T49DES as CHAR(30)) FROM D_PRESTACION_HOMOLOGADA_ACREEDOR AC JOIN HOST03.HOST03.SMPORDD.T149FPRE SA on SA.T49NOM COLLATE SQL_Latin1_General_CP850_CI_AI = AC.id_nomenclador_acreedor and SA.T49PRE COLLATE SQL_Latin1_General_CP850_CI_AI = AC.de_codigo_prestacion_acreedor and SA.T49NPR = AC.id_acreedor
UPDATE D_PRESTACION_HOMOLOGADA_ACREEDOR SET de_prestacion_acreedor = 'S/D' WHERE de_prestacion_acreedor is null

