-- Actualiza en 9999999999 las prestaciones del Acreedor que son iguales a las de Sancor. UPDATE H_ORD_AMB_DET set id_prestacion_acreedor = '9999999999' where id_prestacion = id_prestacion_acreedor and id_periodo in (select MAX(id_periodo) from DW_SALUD.dbo.AUX_PERIODO_MES_BORRA) UPDATE H_ORD_INT_DET set id_prestacion_acreedor = '9999999999' where id_prestacion = id_prestacion_acreedor and id_periodo in (select MAX(id_periodo) from DW_SALUD.dbo.AUX_PERIODO_MES_BORRA)
