/* Actualizo la información existente */ UPDATE D_DIAGNOSTICO SET de_diagnostico = a12.de_diagnostico FROM D_DIAGNOSTICO D Inner join AUX_D_DIAGNOSTICO a12 ON D.id_diagnostico = a12.id_diagnostico
/* Creo la información faltante */ INSERT INTO D_DIAGNOSTICO (id_diagnostico, de_diagnostico, id_marca_severo) SELECT id_diagnostico, de_diagnostico, 'N' FROM AUX_D_DIAGNOSTICO WHERE id_diagnostico not in (SELECT id_diagnostico FROM D_DIAGNOSTICO)
