/* Actualizo la información existente */
UPDATE D_FORMA_PAGO_ORDEN SET de_forma_pago_orden = a12.de_forma_pago_orden FROM D_FORMA_PAGO_ORDEN D Inner Join AUX_D_FORMA_PAGO_ORDEN a12 ON D.id_forma_pago_orden = a12.id_forma_pago_orden
/* Creo la información faltante */
INSERT INTO d_forma_pago_orden (id_forma_pago_orden, de_forma_pago_orden) SELECT id_forma_pago_orden, de_forma_pago_orden FROM AUX_D_FORMA_PAGO_ORDEN WHERE id_forma_pago_orden not in (SELECT id_forma_pago_orden FROM d_forma_pago_orden)
