/* Actualizo la información existente */
UPDATE D_ORIGEN_ENVIO_ORDEN SET de_origen_envio = a12.de_origen_envio FROM D_ORIGEN_ENVIO_ORDEN d Inner join AUX_D_ORIGEN_ENVIO_ORDEN a12 ON D.id_origen_envio_orden = a12.id_origen_envio_orden
/* Creo la información faltante */
INSERT INTO d_origen_envio_orden (id_origen_envio_orden, de_origen_envio) SELECT id_origen_envio_orden, de_origen_envio FROM AUX_D_ORIGEN_ENVIO_ORDEN WHERE id_origen_envio_orden not in (SELECT id_origen_envio_orden FROM d_origen_envio_orden)
