UPDATE D_TIPO_ORDEN SET de_tipo_orden = a12.de_tipo_orden, id_tipo_afiliacion = a12.id_tipo_afiliacion, id_tipo_prestacion = a12.id_tipo_prestacion, id_autorizacion_previa = a12.id_autorizacion_previa, id_atributo_orden = a12.id_atributo_orden FROM D_TIPO_ORDEN D Inner join AUX_D_TIPO_ORDEN a12 ON D.id_tipo_orden = a12.id_tipo_orden
