/* Borra la tabla H_AUDIRED */
TRUNCATE TABLE H_AUDIRED
/* Inserta la nueva información en la tabla H_AUDIRED */
Insert Into H_AUDIRED (Id_Periodo, Id_Posicion_Int, Nro_Pedido, Id_Tipo_Orden, id_Orden_Int, Cant_Visitas_Audired, Fecha_Realizacion, Id_No_Facturador, Id_Tipo_Prestador, Id_Tipo_Internacion, Id_Estado_Aut_Orden, Id_Diagnostico, Id_Producto, Id_Plan_Producto) SELECT I.Id_Periodo, A.Posicion, A.Pedido, A.Tipo_Orden, A.Nro_Orden, 1 as Cant_Visitas_Audired, Cast(Substring(A.Fecha_Realizacion,1,4) as char(4))+'-'+RIGHT(Cast(Substring(A.Fecha_Realizacion,5,2)+100 as char(3)),2)+'-'+Cast(Substring(A.Fecha_Realizacion,7,2) as char(2)), I.Id_No_Facturador, I.Id_Tipo_Prestador, I.Id_Tipo_Internacion, I.Id_Estado_Aut_Orden, I.Id_Diagnostico, I.id_producto, I.id_plan_producto FROM AUX_H_AUDIRED A INNER JOIN H_INTERNACIONES I ON A.Nro_Orden = I.Id_Orden_Int
