/* Actualiza Motivo de Auditoria. */
UPDATE AUX_H_ORDENES_PROCESADAS_DET SET Id_Motivo_Auditoria = M.W01MAU FROM AUX_H_ORDENES_PROCESADAS_DET H JOIN Consultas_DW.DBO.AUX_MOTIVO_AUDITORIA M ON (M.OW1POS = H.Id_Posicion AND M.OW1NOR = H.nro_Orden AND H.Id_Tipo_Orden = M.OW1TOR AND H.Id_Persona = M.OW1NPE)
/* Actualiza Usuario Operador Auditor. */
UPDATE AUX_H_ORDENES_PROCESADAS_DET SET Id_Usuario_Operador_Auditor = HC.ID_Usuario_Operador_Auditor FROM AUX_H_ORDENES_PROCESADAS_DET HD INNER JOIN H_ORDENES_PROCESADAS HC ON HD.Id_periodo = HC.id_periodo AND HD.id_posicion = HC.id_posicion_prestacion AND HD.id_tipo_orden = HC.id_tipo_orden AND HD.nro_orden = HC.nro_orden WHERE HD.Id_Periodo in (Select id_periodo from AUX_PERIODO_MES_BORRA)

/* Actualiza D_USUARIO_CARGA. */
INSERT INTO D_USUARIO_CARGA SELECT rtrim(ltrim(id_usuario)) as id_usuario, MAX('S/D') as De_Usuario_Carga, null as id_categoria_usuario_carga FROM AUX_H_ORDENES_PROCESADAS_DET where rtrim(ltrim(id_usuario)) not in (Select id_usuario_carga from d_usuario_carga) GROUP BY id_usuario
/* Actualiza D_USUARIO_AUDITOR. */
INSERT INTO D_USUARIO_AUDITOR select rtrim(ltrim(id_usuario_auditoria)) as id_usuario_auditoria, MAX('S/D') as De_Usuario_Auditor , null as id_categoria_usuario_auditor from AUX_H_ORDENES_PROCESADAS_DET where rtrim(ltrim(id_usuario_auditoria)) not in (Select id_usuario_auditor from d_usuario_auditor) GROUP BY id_usuario_auditoria
/* Actualizo la tabla de PRESTACION */
INSERT INTO D_PRESTACION (id_prestacion, id_nomenclador, de_codigo_prestacion ,de_prestacion, id_capitulo, id_subcapitulo, id_seccion, id_seguir_prestacion, id_grupo_prestacion, id_subgrupo_prestacion, id_accion_farmacologica, id_servicio_prest, id_catalogada, id_cod_resolucion, id_nivel_capitacion, id_complejidad, id_provision_prest, id_rubro_ind_consumo, id_prestacion_oncologia, id_marca_recupero_sur, simulador_convenio, evolucion_pareto, orden_pareto) SELECT DISTINCT CAST([Id_Nomenclador]as CHAR(2))+ CAST(de_codigo_prestacion AS CHAR(8)) as id_prestacion, id_nomenclador , de_codigo_prestacion, 'F/D' as de_prestacion, 99 as id_capitulo, 99 as id_subcapitulo, 99 as id_seccion, 'N' as id_seguir_prestacion, 99 as id_grupo_prestacion, 99 as id_subgrupo_prestacion, 9999 as id_accion_farmacologica, 99 as id_servicio_prest, 'S' as id_catalogada, 9 as id_cod_resolucion, 999 as id_nivel_capitacion, 999 as id_complejidad, 0 as id_provision_prest, ' ' as id_rubro_ind_consumo, 'N' as id_prestacion_oncologia, 0 as id_marca_recupero_sur, 0 as simulador_convenio, 0 as evolucion_pareto, 0 as orden_pareto FROM AUX_H_ORDENES_PROCESADAS_DET WHERE (CAST([Id_Nomenclador]as CHAR(2))+ CAST(de_codigo_prestacion AS CHAR(8)) ) not in (SELECT distinct Id_Prestacion FROM D_PRESTACION)

/* Declara Periodos a comparar. */
Declare @Periodo_H Int SET @Periodo_H = (select max(id_periodo) from AUX_H_ORDENES_PROCESADAS_DET) Declare @Periodo_MesBorra int SET @Periodo_MesBorra = (Select max(id_periodo) from AUX_PERIODO_MES_BORRA)
/* Si la tabla H es IGUAL a la AUX borra lo que exista. Sino, es DISTINTA entonces informa el error ya que no puede continuar así (Recordar que el RAISERROR continua la ejecución si se coloca un script debajo). */
IF (@Periodo_MesBorra) = (@Periodo_H) begin DELETE FROM H_ORDENES_PROCESADAS_DET WHERE id_periodo = @Periodo_MesBorra end else RAISERROR ('Periodo Proceso Actualización H_ORDENES_PROCESADAS_DET Incorrecto.', 16, 1)
