/* Actualizo la información existente en D_ORIGEN_RECAUDACION */
UPDATE D_ORIGEN_RECAUDACION SET de_origen_recaudacion = a12.de_origen_recaudacion, id_grupo_origen_recaudacion = a12.id_grupo_origen_recaudacion FROM D_ORIGEN_RECAUDACION D INNER JOIN AUX_D_ORIGEN_RECAUDACION a12 ON D.id_origen_recaudacion = a12.id_origen_recaudacion
/* Actualizo la información faltante en D_ORIGEN_RECAUDACION */
INSERT INTO D_ORIGEN_RECAUDACION (id_origen_recaudacion, de_origen_recaudacion, id_grupo_origen_recaudacion) SELECT id_origen_recaudacion, de_origen_recaudacion, id_grupo_origen_recaudacion FROM AUX_D_ORIGEN_RECAUDACION WHERE id_origen_recaudacion not in (SELECT distinct id_origen_recaudacion FROM d_origen_recaudacion)
/* Actualiza el id_grupo_origen en la D_ORIGEN_RECAUDACION */
UPDATE D_ORIGEN_RECAUDACION SET id_Grupo_origen_recaudacion = 'M' WHERE id_origen_recaudacion = 13
UPDATE D_ORIGEN_RECAUDACION SET id_Grupo_origen_recaudacion = 'X' WHERE id_Grupo_origen_recaudacion = ''
