Declare @Periodo int SET @Periodo = (select max(id_periodo) from AUX_PERIODO_MES_BORRA)
/* Borra la tabla FONDO_DE_RESERVA */
Truncate Table Consultas_DW.dbo.FONDO_DE_RESERVA
/* Inserta en la tabla FONDO_DE_RESERVA con la información del Excel */
Insert into Consultas_DW.dbo.FONDO_DE_RESERVA (id_periodo, afiliado, subnumero, id_origen_recaudacion, importe) select @Periodo as id_periodo, afiliado, MAX(subnumero) as subnumero, id_origen_recaudacion, sum(importe) as importe from Consultas_DW.dbo.AUXILIAR_FONDO_DE_RESERVA group by id_periodo, afiliado, id_origen_recaudacion
/* Borra todos los importe registros con importe 0 */
Delete from Consultas_DW.dbo.FONDO_DE_RESERVA where importe = 0
