Declare @PeriodoRecaudacion Int SET @PeriodoRecaudacion = (Select max(id_periodo) from AUX_PERIODO_MES_BORRA)
/* Borra los registos de AFIP en H_RECAUDACION_DETALLE */
Delete from H_RECAUDACION_DETALLE Where id_periodo = @PeriodoRecaudacion and id_fuente_recaudacion = 10
/* Inserta los nuevos registos de AFIP en H_RECAUDACION_DETALLE */
INSERT INTO H_RECAUDACION_DETALLE (id_periodo, afiliado, subnumero, secuencia, id_persona, id_origen_recaudacion, id_producto, id_subproducto, id_plan_producto, id_entidad, id_localidad, edad_actual, total_recaudacion, grilla, fecha_ingreso_segmento, fecha_ingreso_salud, id_categoria, id_empresa_recaudacion, id_tipo_entidad_recaudacion, rcosto1, rcosto2, rcosto3, rcosto4, id_producto_original, id_subproducto_original, id_plan_producto_original, grilla_original, id_periodo_liquidacion, id_factura_A_E_rec, id_empresa_PE_rec, id_tipo_entidad_PE_rec, id_marca_PE_rec, id_convenio_especial_rec, id_periodo_consumo_M, id_rango_edad_rec, id_fuente_recaudacion) SELECT id_periodo, afiliado, subnumero, secuencia, id_persona, id_origen_recaudacion, id_producto, id_subproducto, id_plan_producto, id_entidad, id_localidad, edad_actual, total_recaudacion, grilla, fecha_ingreso_segmento, fecha_ingreso_salud, id_categoria, id_empresa_recaudacion, id_tipo_entidad_recaudacion, rcosto1, rcosto2, rcosto3, rcosto4, id_producto_original, id_subproducto_original, id_plan_producto_original, grilla_original, id_periodo_liquidacion, id_factura_A_E_rec, id_empresa_PE_rec, id_tipo_entidad_PE_rec, id_marca_PE_rec, id_convenio_especial_rec, id_periodo_consumo_M, id_rango_edad_rec, 10 as id_fuente_recaudacion from consultas_dw.dbo.H_RECAUDACION_DETALLE_AFIP where @PeriodoRecaudacion = id_periodo
