declare @Periodo int set @Periodo = (SELECT MAX(id_periodo) from CONSULTAS_DW.DBO.AUX_PERIODO_MES_BORRA_LIQUIDACIONES)
/* Busco el maximo periodo para cada afiliado de con cuenta corriente especial */ select H.afiliado,max(id_periodo) max_periodo, null as id_persona, null as id_producto, ' ' as id_plan_producto, null as id_subproducto, null as subnumero, null as nro_afiliado_origen, NULL AS secuencia into #CUENTAS_SALUD from AUX_CTA_CTE_ESPECIAL_DET A join H_COMER_GRUPO_ACTIVO H on (A.afiliado = H.Afiliado) where H.id_producto in (select id_producto from D_PRODUCTOS where id_clase_producto = 'S') group by H.afiliado
/* Actualizo los datos necesarios para incluirlos en H_LIQUIDACIONES */ update #CUENTAS_SALUD set id_persona = H.id_persona, id_producto = H.id_producto, id_plan_producto = H.id_plan_producto, id_subproducto = H.id_subproducto, subnumero = H.Subnumero, secuencia = ISNULL(H.secuencia,0) from H_COMER_GRUPO_ACTIVO H join #CUENTAS_SALUD C on (H.afiliado = C.afiliado) where H.id_periodo = C.max_periodo
/* Actualizo nro afiliado origen */ update #CUENTAS_SALUD set nro_afiliado_origen = L.nro_afiliado_origen FROM #CUENTAS_SALUD CS join H_COMER_STOCK_AFILIA L on (CS.afiliado = L.afiliado) where L.id_periodo = CS.max_periodo
/* Inserto en H_LIQUIDACIONES la informacion */ INSERT INTO H_LIQUIDACIONES ([id_periodo] ,[id_tipo_cuenta] ,[id_empresa] ,[id_comprobante_liquidacion] ,[id_talonario_liquidacion] ,[numero_interno_liquidacion] ,[id_persona] ,[id_producto] ,[id_plan_producto] ,[id_subproducto] ,[id_tipo_aporte_liquidacion] ,[id_concepto_liquidacion] ,[afiliado] ,[subnumero] ,[secuencia] ,[nro_afiliado_origen] ,[total_concepto] ,[id_periodo_liquidacion] ,[id_categoria_liquidacion] ,[fecha_comprobante_liquidacion] , id_fuente_liquidacion) SELECT @Periodo , A.Tipo_cuenta, 0,--ID_EMPRESA 'SOBE',--COMPROBANTE 999,--ID_TALONARIO_LIQUIDACION 0,--NRO INTERNO LIQUIDACION H.id_persona, H.id_producto, H.id_plan_producto, H.id_subproducto, 'XX',--ID TIPO APORTE 999998,--ID_CONCEPTO LIQUIDACION A.afiliado, H.Subnumero, H.secuencia, H.nro_afiliado_origen,--NRO AFILIADO ORIGEN A.Importe_acumulado, DW_SALUD.dbo.DevuelvePeriodo(A.fecha_movimiento), 'SOE',--categoria A.fecha_movimiento , 2 as id_fuente_liquidacion FROM AUX_CTA_CTE_ESPECIAL_DET A JOIN #CUENTAS_SALUD H ON (H.afiliado = A.afiliado)
drop table #CUENTAS_SALUD
UPDATE H_LIQUIDACIONES SET marca_sob_periodo_liquidacion = 1 WHERE id_periodo = @Periodo AND id_periodo_liquidacion = @Periodo AND id_comprobante_liquidacion IN ('SOBE')
