/*ACTUALIZO LOS QUE YA EXISTEN Y SUFRIERON CAMBIOS EN LA PARAMETRIZACION DE LAS CATEGORIAS*/
select W47CON, W47DES, W47CAT into #TEMP from HOST03.HOST03.SMPLIBD.T47FCON
ALTER TABLE #TEMP ALTER COLUMN W47CON varchar(50) COLLATE SQL_Latin1_General_CP850_CI_AI NULL ALTER TABLE #TEMP ALTER COLUMN W47DES varchar(50) COLLATE SQL_Latin1_General_CP850_CI_AI NULL ALTER TABLE #TEMP ALTER COLUMN W47CAT varchar(50) COLLATE SQL_Latin1_General_CP850_CI_AI NULL
update D_CONCEPTOS_LIQUIDACION set id_categoria_liquidacion = W47CAT, de_concepto_liquidacion = W47DES from #TEMP join D_CONCEPTOS_LIQUIDACION on (id_concepto_liquidacion = W47CON ) where id_categoria_liquidacion <> W47CAT and W47CAT <> '999999'
/*INSERTO EN LA TABLA LOS NUEVOS CONCEPTOS DE LIQUIDACION*/ INSERT INTO D_CONCEPTOS_LIQUIDACION ([id_concepto_liquidacion], [de_concepto_liquidacion], [id_categoria_liquidacion]) select W47CON, W47DES, W47CAT from #TEMP where W47CON not in (select id_concepto_liquidacion from D_CONCEPTOS_LIQUIDACION)
