/* COMPROBANTES DE LIQUIDACION */

if (select COUNT(*) from (SELECT distinct id_comprobante_liquidacion from AUX_H_LIQUIDACIONES
						  where id_comprobante_liquidacion not in 
						  (select id_comprobante_liquidacion 
						   from D_COMPROBANTES_LIQUIDACION)) as Consulta) > 0  
begin

		EXEC msdb.dbo.sp_send_dbmail
			@profile_name = 'Soporte MicroStrategy',
			@recipients = 'juanfrancisco.trinchieri@sancorsalud.com.ar; nicolas.prida@sancorsalud.com.ar; lorena.assone@sancorsalud.com.ar; nicolas.borio@sancorsalud.com.ar',
			@subject = 'Nuevos comprobantes de Liquidaciones',
			@body = 'Se han dado de alta los siguientes Comprobates de Liquidaciones:',
			@query = 'SELECT distinct id_comprobante_liquidacion 
				      from AUX_H_LIQUIDACIONES
					  where id_comprobante_liquidacion not in (select id_comprobante_liquidacion from D_COMPROBANTES_LIQUIDACION)',
			@query_result_separator = ';',
			@execute_query_database = 'DW_SALUD';


end


/* CATEGORIAS DE LIQUIDACION */

if (select COUNT(*) from (select distinct id_categoria_liquidacion 
						  from AUX_H_LIQUIDACIONES 
						  where id_categoria_liquidacion not in (select id_categoria_liquidacion from D_CATEGORIAS_LIQUIDACION)) as Consulta) > 0  
begin

		EXEC msdb.dbo.sp_send_dbmail
			@profile_name = 'Soporte MicroStrategy',
			@recipients = 'juanfrancisco.trinchieri@sancorsalud.com.ar; nicolas.prida@sancorsalud.com.ar;  lorena.assone@sancorsalud.com.ar; nicolas.borio@sancorsalud.com.ar',
			@subject = 'Categorias de Liquidacion',
			@body = 'Se han dado de alta las siguientes Categorias de Liquidaciones:',
			@query = 'select distinct id_categoria_liquidacion 
						  from AUX_H_LIQUIDACIONES 
						  where id_categoria_liquidacion not in (select id_categoria_liquidacion from D_CATEGORIAS_LIQUIDACION)',
			@query_result_separator = ';',
			@execute_query_database = 'DW_SALUD';


end
