DECLARE @Ult_Periodo Int SET @Ult_Periodo = (SELECT max(id_periodo) FROM DW_SALUD.dbo.H_COMER_STOCK_AFILIA)
/* Actualizo H_DEUDA_DOS_MESES */
INSERT INTO Consultas_DW.dbo.H_DEUDA_DOS_MESES (Id_periodo, Apellido, Nombre, id_persona, afiliado, subnumero, fecha_nacimiento, tipo_documento, Nro_documento, Calle_persona, Dpto_persona, Nro_puerta_persona, Caracteristica_te, Nro_telefono, id_localidad_afiliado, de_localidad, id_provincia, de_provincia, de_zona_comercial, de_posicion, edad_periodo, id_plan_producto, fecha_ingreso_sancor_salud, fecha_ingreso_segmento_comercial, saldo,id_zona_comercial,id_producto) SELECT (@Ult_Periodo) as Id_periodo, P.Apellido, P.Nombre, S.id_persona, S.afiliado, S.subnumero, P.fecha_nacimiento, P.tipo_documento, P.Nro_documento, P.Calle_persona, P.Dpto_persona,P.Nro_puerta_persona,P.Caracteristica_te,P.Nro_telefono, P.id_localidad_afiliado, L.de_localidad, PR.id_provincia, PR.de_provincia, Z.de_zona_comercial, PO.de_posicion, ST.edad_periodo, PL.id_plan_producto, ST.fecha_ingreso_sancor_salud, ST.fecha_ingreso_segmento_comercial,sum(S.saldo)as saldo, Z.id_zona_comercial,ST.id_producto FROM Consultas_DW.dbo.AUX_H_DEUDA_DOS_MESES S INNER JOIN DW_SALUD.dbo.D_PERSONAS P on P.id_persona = S.id_persona INNER JOIN DW_SALUD.dbo.D_LOCALIDADES L on L.id_localidad = P.id_localidad_afiliado INNER JOIN DW_SALUD.dbo.D_PROVINCIA PR on PR.id_provincia = L.id_provincia INNER JOIN DW_SALUD.dbo.D_ZONA_COMERCIAL Z on Z.id_zona_comercial = L.id_zona_comercial INNER JOIN DW_SALUD.dbo.D_POSICION PO on PO.id_posicion = L.id_posicion INNER JOIN DW_SALUD.dbo.H_COMER_STOCK_AFILIA ST on ST.id_persona = S.id_persona and ST.Afiliado = S.Afiliado INNER JOIN DW_SALUD.dbo.D_PLANES PL on PL.id_plan_producto = ST.id_plan_producto --INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M20FPAD] FO on ST.afiliado = FO.W20NAF and ST.subnumero = FO.W20SAF --INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M07FPAG] PAG on FO.W20NAF = PAG.WP7CTA and FO.W20NPP = PAG.WP7NPA WHERE ST.Id_periodo = @Ult_Periodo AND ST.edad_periodo BETWEEN 18 AND 45 AND ST.id_producto NOT IN(39) AND ST.id_subproducto NOT IN(39) AND P.Nro_telefono NOT IN ('') AND P.Nro_telefono IS NOT NULL GROUP BY P.Apellido, P.Nombre, S.id_persona, S.afiliado, S.subnumero, S.Apellido, S.Nombre, P.fecha_nacimiento, P.tipo_documento, P.Nro_documento, P.Calle_persona, P.Dpto_persona, P.Nro_puerta_persona, P.Caracteristica_te, P.Nro_telefono, P.id_localidad_afiliado, L.de_localidad, PR.id_provincia, PR.de_provincia, Z.id_zona_comercial, Z.de_zona_comercial, PO.de_posicion, ST.edad_periodo, ST.id_subproducto, ST.id_producto, PL.id_plan_producto, ST.fecha_ingreso_sancor_salud, ST.fecha_ingreso_segmento_comercial
/* Calculo Subnumero y Secuencia */
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Secuencia = H.Secuencia, Subnumero = H.Subnumero FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_COMER_STOCK_AFILIA H on M.Id_persona = H.Id_persona WHERE H.Id_periodo = @Ult_Periodo
/* Actualizo SubProducto */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET De_subproducto = SU.De_subproducto FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_COMER_STOCK_AFILIA H on M.Id_persona = H.Id_persona INNER JOIN DW_SALUD.dbo.D_SUBPRODUCTO SU on SU.Id_Subproducto = H.Id_Subproducto and SU.id_producto = H.id_producto WHERE H.id_periodo = @Ult_Periodo
/* Actualizo Grilla */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET grilla = G.grilla FROM DW_SALUD.dbo.H_COMER_STOCK_AFILIA S INNER JOIN Consultas_DW.dbo.H_DEUDA_DOS_MESES M on M.id_persona = S.id_persona INNER JOIN DW_SALUD.dbo.D_GRILLAS G on g.grilla = S.grilla and G.id_producto = S.id_producto and G.id_plan_producto = S.id_plan_producto WHERE S.id_periodo = @Ult_Periodo
/* Actualizo Descripcion de Grilla */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET De_grilla = G.De_grilla FROM DW_SALUD.dbo.H_COMER_STOCK_AFILIA S INNER JOIN Consultas_DW.dbo.H_DEUDA_DOS_MESES M on M.id_persona = S.id_persona INNER JOIN DW_SALUD.dbo.D_GRILLAS G on G.grilla = S.grilla and G.id_producto = S.id_producto and G.id_plan_producto = S.id_plan_producto WHERE S.id_periodo = @Ult_Periodo
/* Actualizo Periodo Alerta */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Periodo_Alerta = A.Id_Periodo FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado
/* Actualizo Motivo Alertas */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET De_motivo_alerta = MO.De_motivo_alerta FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado INNER JOIN DW_SALUD.dbo.D_MOTIVOS_ALERTA MO on MO.id_motivo_alerta = A.id_motivo_alerta
/* Actualizo Observaciones Alertas */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET observaciones_alerta = A.observaciones FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_LLAVES_ALERTAS_AFILIADOS A on A.afiliado = M.afiliado
/* Calculo el Mail */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Mail = MA.W03NRO FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES S LEFT OUTER JOIN [HOST03].[HOST03].[SMPLIBD].[M03FTEL] MA on S.id_persona = MA.W03NPE WHERE MA.W03TIT = 'E'
/* Calculo el Forma de Pago */ SELECT D.id_persona, D.Afiliado, FP.WP7FPA as forma_pago, F.W17DES as forma_pago_desc INTO #Forma_Pago FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES D join HOST03.HOST03.SMPLIBD.M07FPAG FP on D.Afiliado = FP.WP7CTA join HOST03.HOST03.SMPLIBD.T17FFPA F on F.W17FPA = FP.WP7FPA WHERE FP.WP7FPA NOT IN ('EF') GROUP BY D.id_persona, D.Afiliado, FP.WP7FPA, F.W17DES
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET forma_pago = FP.forma_pago, forma_pago_desc = FP.forma_pago_desc FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES D JOIN #Forma_Pago FP on D.Afiliado = FP.Afiliado and D.id_persona = FP.id_persona
/* Calculo Costo Salud */
SELECT S.id_persona, S.afiliado INTO Personas_por_Afiliado_DOS_MESES FROM DW_SALUD.dbo.h_comer_stock_afilia S INNER JOIN Consultas_DW.dbo.H_DEUDA_DOS_MESES D on D.Afiliado = S.Afiliado WHERE S.id_periodo = @Ult_Periodo GROUP BY S.id_persona, S.Afiliado
/* Sumo la recaudacion */
SELECT a11.id_persona, sum(a11.total_recaudacion) Total_Recaudación INTO SumReca_DOS_M FROM DW_SALUD.dbo.H_RECAUDACION_DETALLE a11 join DW_SALUD.dbo.D_PLANES a12 on (a11.id_plan_producto = a12.id_plan_producto and a11.id_producto = a12.id_producto) join DW_SALUD.dbo.D_PRODUCTOS a13 on (a12.id_producto = a13.id_producto) join Personas_por_Afiliado_DOS_MESES P on P.id_persona = a11.id_persona WHERE a11.id_periodo in (SELECT top 12 id_periodo FROM DW_SALUD.dbo.d_periodo WHERE vigente_consumo = 1 ORDER BY id_periodo desc) and not exists (select fpp.id_producto,fpp.id_plan_producto from DW_SALUD.dbo.FILTRO_PLAN_PRODUCTO fpp where fpp.id_plan_producto = a11.id_plan_producto and fpp.id_producto = a11.id_producto) and rcosto3 = 1 GROUP BY a11.id_persona
/* Sumo el consumo ambulatorio */
SELECT a11.id_persona, sum(a11.imp_pret_amb_pag) WJXBFS1 INTO SumConsuAMB_DOS_M FROM DW_SALUD.dbo.H_ORD_AMB_DET a11 join DW_SALUD.dbo.D_PRODUCTOS a12 on (a11.id_producto = a12.id_producto) join Personas_por_Afiliado_DOS_MESES P on P.id_persona = a11.id_persona WHERE a11.id_periodo in (SELECT top 12 id_periodo FROM DW_SALUD.dbo.d_periodo WHERE vigente_consumo = 1 ORDER BY id_periodo desc) and not exists (select fpp.id_producto,fpp.id_plan_producto from DW_SALUD.dbo.FILTRO_PLAN_PRODUCTO fpp where fpp.id_plan_producto = a11.id_plan_producto and fpp.id_producto = a11.id_producto) and ccosto3 = 1 GROUP BY a11.id_persona
/* Sumo el consumo internaciones */
SELECT a11.id_persona, sum(a11.imp_prest_int_pag) WJXBFS1 INTO SumConsuINT_DOS_M FROM DW_SALUD.dbo.H_ORD_INT_DET a11 join DW_SALUD.dbo.D_PRODUCTOS a12 on (a11.id_producto = a12.id_producto) join Personas_por_Afiliado_DOS_MESES P on P.id_persona = a11.id_persona WHERE a11.id_periodo in (SELECT top 12 id_periodo FROM DW_SALUD.dbo.d_periodo WHERE vigente_consumo = 1 ORDER BY id_periodo desc) and not exists (select fpp.id_producto,fpp.id_plan_producto from DW_SALUD.dbo.FILTRO_PLAN_PRODUCTO fpp where fpp.id_plan_producto = a11.id_plan_producto and fpp.id_producto = a11.id_producto) and ccosto3 = 1 GROUP BY a11.id_persona
/* Armo Tabla con Recaudacion y Consumos */
SELECT coalesce(pa11.id_persona, pa12.id_persona, pa13.id_persona) id_persona, pa11.Total_Recaudación Total_Recaudación, pa12.WJXBFS1 WJXBFS2, pa13.WJXBFS1 WJXBFS3 INTO Costo_Salud_TOTAL_DOS_M FROM SumReca_DOS_M pa11 full outer join SumConsuAMB_DOS_M pa12 on (pa11.id_persona = pa12.id_persona) full outer join SumConsuINT_DOS_M pa13 on (coalesce(pa11.id_persona, pa12.id_persona) = pa13.id_persona) join DW_SALUD.dbo.D_PERSONAS a14 on (coalesce(pa11.id_persona, pa12.id_persona, pa13.id_persona) = a14.id_persona)
------------------------------------------------------------
/* Pego el Afiliado en los valores de Persona */
select TC.afiliado, sum(IsNull(C.total_recaudacion,0)) as Total_recaudacion, sum(IsNull(C.WJXBFS2,0)) as Total_Consumo_AMB, sum(IsNull(C.WJXBFS3,0)) as Total_Consumo_INT INTO TMP_Costo_Salud_2M from Costo_Salud_TOTAL_DOS_M C inner join Personas_por_Afiliado_DOS_MESES TC on TC.id_persona = C.id_persona group by TC.afiliado
------------------------------------------------------------
/* Pego el valor del Costo de Salud, Consumo y Recaudacion en la tabla H_DEUDA_DOS_MESES*/
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Recaudacion = CAST((C.Total_Recaudacion) as Decimal (12,3)) --C.Total_Recaudacion FROM TMP_Costo_Salud_2M C INNER JOIN Consultas_DW.dbo.H_DEUDA_DOS_MESES M on C.Afiliado = M.Afiliado
SELECT C.Afiliado, sum(CAST((C.Total_Consumo_AMB) as Decimal (12,3)) + CAST((C.Total_Consumo_INT) as Decimal (12,3))) as Consumos INTO Consumos_Dos_meses FROM TMP_Costo_Salud_2M C GROUP BY C.Afiliado
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Consumos = CD.Consumos FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES C INNER JOIN Consumos_Dos_meses CD on CD.Afiliado = C.Afiliado
------------------------------------------------------------
/* Pongo en 1 las recaudaciones que estan en 0 para que no tire error en la división */
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Recaudación = 1 WHERE Recaudación = 0 or Recaudación is null
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Consumos = 1 WHERE Consumos = 0 or Consumos is null
/* Hago la división */
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud = CAST((Consumos/Recaudacion) as Decimal (12,3))-- (Consumos)/(Recaudacion) FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES
------------------------------------------------------------
/* Multiplico para sacar el porcentaje */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud = M.Costo_Salud * 100 FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud = 1 WHERE Recaudación = 1 and Consumos = 1
/* Lleno la columna costo_salud_porcent con la informacion de costo_salud*/ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud_Porcent = M.Costo_Salud FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M
/* Elimino los registros que tengan un costo de salud > 75*/ DELETE FROM H_DEUDA_DOS_MESES WHERE Costo_Salud > 75
/* Concateno la columna costo_salud_porcent con el % */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud_Porcent = (CAST(M.Costo_Salud_Porcent as char(8))+'%') FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M
/* Saco Espacios en la concatenacion de Costo_Salud_Porcent */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud_Porcent = REPLACE(Costo_Salud_Porcent, ' ','') FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M
/* Remplace null por 0 */ UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Costo_Salud_Porcent = 0 FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M WHERE Costo_Salud_Porcent is null
/* Calculo cantidad grupo familiar */
SELECT M.afiliado, Count(ST.id_persona) as Cantidad_Grupo, M.id_persona INTO Grupo_Familiar_DOS_M FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN DW_SALUD.dbo.H_COMER_STOCK_AFILIA ST on M.afiliado = ST.afiliado WHERE ST.id_periodo = @Ult_Periodo GROUP BY M.Afiliado, M.Id_persona ORDER BY M.Afiliado
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Cant_Grupo_Familiar = ST.Cantidad_Grupo FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES M INNER JOIN Grupo_Familiar_DOS_M ST on M.Afiliado = ST.Afiliado
/* Calculo Forma de Pago */
--update Consultas_DW.dbo.H_DEUDA_DOS_MESES --set Forma_Pago = PAG.WP7FPA --from Consultas_DW.dbo.H_DEUDA_DOS_MESES ST --INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M20FPAD] FO on ST.afiliado = FO.W20NAF and ST.subnumero = FO.W20SAF --INNER JOIN [HOST03].[HOST03].[SMPLIBD].[M07FPAG] PAG on FO.W20NAF = PAG.WP7CTA and FO.W20NPP = PAG.WP7NPA
/* Calculo Estado
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Estado = 'Se Envia Mensaje' FROM Consultas_DW.dbo.H_DEUDA_DOS_MESES WHERE Id_persona in (SELECT id_persona FROM Consultas_DW.dbo.H_DEUDA_MENSAJES)
UPDATE Consultas_DW.dbo.H_DEUDA_DOS_MESES SET Estado = 'No se envia Mensaje' WHERE Estado is null */
