SET DATEFORMAT 'YMD'
DECLARE @Fecha_Periodo DateTime DECLARE @Periodo CHAR(6) DECLARE @Periodo_Uno CHAR(6) DECLARE @Periodo_Dos CHAR(6) DECLARE @Periodo_Tres CHAR(6)
/* Tomo el Mes de Proceso en Formato INT */ SELECT @Periodo = CAST(MAX(Id_periodo) AS CHAR(6)) from DW_SALUD.dbo.AUX_PERIODO_MES_BORRA
/* Tranformo el Mes de Proceso en Formato Datetime */ SET @Fecha_Periodo = SUBSTRING(@Periodo, 1, 4)+'-'+SUBSTRING(@Periodo, 5, 2)+'-'+'01 '
/* Calculo los periodos anteriores desde la @Fecha_Periodo */ SET @Periodo_Uno = (DW_SALUD.dbo.PeriodoAnterior(@Fecha_Periodo)) SET @Periodo_Dos = (DW_SALUD.dbo.Periodo2MesesAtras(@Fecha_Periodo)) SET @Periodo_Tres = (DW_SALUD.dbo.Periodo3MesesAtras(@Fecha_Periodo))
--/* Actualizo Tabla Deuda Total que tiene todas los afiliados con el periodo al que pertenecen y tienen deuda */
/* Actualizo Tablas Auxiliares de Deudas Dos y Tres Meses ((Esta separada la condicion debajo con la de arriba ya que no me deja hacer una subconsulta entre dos Tablas AS400)) */
Insert Into Consultas_DW.dbo.AUX_H_DEUDA_DOS_MESES (id_periodo, id_persona, Afiliado, saldo) Select (@Periodo) as Periodo, DEU.id_persona, DEU.Afiliado, sum(DEU.saldo) as saldo FROM Consultas_DW.dbo.H_DEUDA_TOTAL DEU where DEU.Periodo_Comprobante <> @Periodo_Tres and DEU.afiliado in (select afiliado from Consultas_DW.dbo.H_DEUDA_TOTAL where (Substring(CAST(Periodo_Comprobante as CHAR(8)),1,6)) = @Periodo_Uno) and DEU.afiliado in (select afiliado from Consultas_DW.dbo.H_DEUDA_TOTAL where (Substring(CAST(Periodo_Comprobante as CHAR(8)),1,6)) = @Periodo_Dos) and DEU.afiliado not in (select afiliado from Consultas_DW.dbo.H_DEUDA_TOTAL where (Substring(CAST(Periodo_Comprobante as CHAR(8)),1,6)) < @Periodo_Dos) group by DEU.id_persona, DEU.Afiliado
