/* Inicializar datos de tipo deudor = Obra Social y tipo cuenta = Empresa */
update AUX_H_COBRANZAS set id_tipo_deudor = 'O', id_tipo_cuenta = 'E'
/* Busqueda de id_persona en nuestra bases de personas */
update AUX_H_COBRANZAS set id_persona = PER.id_persona from AUX_H_COBRANZAS COB inner join D_PERSONAS PER on (PER.cuil = COB.cuil_persona_cobranza)
update AUX_H_COBRANZAS set id_persona = 0 where id_persona is null
/* Inicializar datos de Producto */
update AUX_H_COBRANZAS set id_producto = 0, id_subproducto = 0, id_plan_producto = ' '
/* verificar si no encuentro el id_empresa teniendo el cuit de la misma */
update AUX_H_COBRANZAS set id_empresa = EMP.id_empresa from AUX_H_COBRANZAS COB inner join D_EMPRESAS EMP on (EMP.cuit_empresa = COB.cuit_empresa_cobranza) WHERE COB.id_empresa = 0
/* Actualizar el tipo comprobante deuda, talonario y nro comprobante que no existen */ /* Además actualizar total_comprobante_Debito_deuda */
update AUX_H_COBRANZAS set id_tipo_comprobante_deuda = ' ', id_talonario_cobranza = 0, total_comprobante_debito_deuda = 0, id_forma_de_pago = ' ', id_marca_incobrable = 'X', meses_antiguedad_afiliado_cobranza = 0, id_marca_AJUA = ' ', rango_antiguedad_en_meses_cobranza = 0, id_marca_moroso = 'X', id_tipo_comprobante_aplicacion_cobranza = ' ', dias_mora = 0, id_rango_mora = 0, fecha_ingreso_segmento_cobranza = 0, fecha_comprobante_cobranza = 0, fecha_vencimiento_cobranza = 0
/* Actualizar campo de aplicaciones */
update AUX_H_COBRANZAS set total_comprobante_credito_aplicado = total_comprobante_credito_aplicado + total_comprobante_debito_aplicado, total_comprobante_debito_aplicado = 0
/* Formateo Fecha ultima aplicacion en formato fecha */
update aux_h_cobranzas set fecha_ultima_aplicacion = cast( substring(fecha_ultima_aplicacion_origen,1,4)+'-'+substring(fecha_ultima_aplicacion_origen,5,2)+'-'+substring(fecha_ultima_aplicacion_origen,7,2)+' ' as datetime) where fecha_ultima_aplicacion_origen <> '00000000'
update aux_h_cobranzas set id_periodo_ultima_aplicacion = substring(fecha_ultima_aplicacion_origen,1,6)
