----- Envia Mail si existe alguna id_tipo_comprobante_deuda_cobranza que no esta en D_TIPO_COMPROBANTE_DEUDA_COBRANZA

if (select COUNT(id_tipo_comprobante_deuda_cobranza) from H_COBRANZAS
where id_tipo_comprobante_deuda_cobranza not in (select distinct id_tipo_comprobante_deuda_cobranza from D_TIPO_COMPROBANTE_DEUDA_COBRANZA)) > 0

begin 
	EXEC msdb.dbo.sp_send_dbmail
				@profile_name = 'Soporte Microstrategy',
				@recipients = 'liliana.alassia@sancorsalud.com.ar; juanfrancisco.trinchieri@sancorsalud.com.ar',
				@subject = 'Nuevo Tipo Comprobante Deuda Cobranza - Data Deuda y Cobranza',
				@body = 'Se detectaron los siguientes tipos de comprobante deuda cobranza que no estan presentes en la tabla D_TIPO_COMPROBANTE_DEUDA_COBRANZA:

				',
				@query = 'select id_tipo_comprobante_deuda_cobranza from H_COBRANZAS
where id_tipo_comprobante_deuda_cobranza not in (select id_tipo_comprobante_deuda_cobranza from D_TIPO_COMPROBANTE_DEUDA_COBRANZA group by id_tipo_comprobante_deuda_cobranza)
GROUP BY id_tipo_comprobante_deuda_cobranza',
				@query_result_separator = '|',
				@execute_query_database = 'DW_SALUD';
end



----- Envia Mail si existe algun tipo de comprobante de Aplicación que no está definido en D_TIPO_COMPROBANTE_APLICACION_COBRANZA 

if (select COUNT(id_tipo_comprobante_aplicacion_cobranza)  from H_COBRANZAS
where id_tipo_comprobante_aplicacion_cobranza not in (select distinct id_tipo_comprobante_aplicacion_cobranza from D_TIPO_COMPROBANTE_APLICACION_COBRANZA))  > 0

begin 
	EXEC msdb.dbo.sp_send_dbmail
				@profile_name = 'Soporte Microstrategy',
				@recipients = 'liliana.alassia@sancorsalud.com.ar; juanfrancisco.trinchieri@sancorsalud.com.ar',
				@subject = 'Nuevo Tipo Comprobante Aplicación Cobranza - Data Deuda y Cobranza',
				@body = 'Se detectaron los siguientes tipos de comprobante aplicación cobranza que no estan presentes en la tabla D_TIPO_COMPROBANTE_APLICACION_COBRANZA:

				',
				@query = 'select id_tipo_comprobante_aplicacion_cobranza from H_COBRANZAS
where id_tipo_comprobante_aplicacion_cobranza not in (select id_tipo_comprobante_aplicacion_cobranza from D_TIPO_COMPROBANTE_APLICACION_COBRANZA group by id_tipo_comprobante_aplicacion_cobranza)
GROUP BY id_tipo_comprobante_aplicacion_cobranza',
				@query_result_separator = '|',
				@execute_query_database = 'DW_SALUD';
end
